00053 static inline void put_pixel(PIO pio, uint sm, uint32_t pixel_grb)
00054 {
00055     pio_sm_put_blocking(pio, sm, pixel_grb << 8u);
00056 }
00057 
00058 static inline uint32_t urgb_u32(uint8_t r, uint8_t g, uint8_t b)
00059 {
00060     return
00061         ((uint32_t)(r) << 8) |
00062         ((uint32_t)(g) << 16) |
00063         (uint32_t)(b);
00064 }
00065 
00066 static inline uint32_t urgbw_u32(uint8_t r, uint8_t g, uint8_t b, uint8_t w)
00067 {
00068     return
00069         ((uint32_t)(r) << 8) |
00070         ((uint32_t)(g) << 16) |
00071         ((uint32_t)(w) << 24) |
00072         (uint32_t)(b);
00073 }
