00075 void pattern_snakes(PIO pio, uint sm, uint len, uint t)
00076 {
00077     for (uint i = 0; i < len; ++i)
00078     {
00079         uint x = (i + (t >> 1)) % 64;
00080         if (x < 10)
00081             put_pixel(pio, sm, urgb_u32(0xff, 0, 0));
00082         else if (x >= 15 && x < 25)
00083             put_pixel(pio, sm, urgb_u32(0, 0xff, 0));
00084         else if (x >= 30 && x < 40)
00085             put_pixel(pio, sm, urgb_u32(0, 0, 0xff));
00086         else
00087             put_pixel(pio, sm, 0);
00088     }
00089 }
00090 
00091 void pattern_random(PIO pio, uint sm, uint len, uint t)
00092 {
00093     if (t % 8)
00094         return;
00095     for (uint i = 0; i < len; ++i)
00096         put_pixel(pio, sm, urgbw_u32(rand() / 100000, rand() / 100000, rand() / 100000, rand() / 100000));
00097 }
00098 
00099 void pattern_sparkle(PIO pio, uint sm, uint len, uint t)
00100 {
00101     if (t % 8)
00102         return;
00103     for (uint i = 0; i < len; ++i)
00104         put_pixel(pio, sm, rand() % 16 ? 0 : 0xffffffff);
00105 }
00106 
00107 void pattern_greys(PIO pio, uint sm, uint len, uint t)
00108 {
00109     uint max = 100; // let's not draw too much current!
00110     t %= max;
00111     for (uint i = 0; i < len; ++i)
00112     {
00113         put_pixel(pio, sm, t * 0x10101);
00114         if (++t >= max) t = 0;
00115     }
00116 }
