00343 static void blit_rect_dma(uint8_t* framebuffer)
00344 {
00345     // int dma_chan = dma_claim_unused_channel(true);
00346     // dma_channel_config cfg = dma_channel_get_default_config(dma_chan);
00347     // channel_config_set_transfer_data_size(&cfg, DMA_SIZE_8);
00348     // channel_config_set_read_increment(&cfg, true);
00349     // channel_config_set_write_increment(&cfg, true);
00350 
00351     // Set up interpolator to compute row base addresses
00352     interp_config icfg = interp_default_config();
00353     interp_config_set_add_raw(&icfg, true);
00354     interp_config_set_shift(&icfg, 0);
00355     interp_config_set_mask(&icfg, 0, 31); // full 32-bit mask
00356     interp_set_config(interp0, 0, &icfg);
00357 
00358 
00359     int src_index = 0;
00360 
00361     for (int row = 0; row < 100; row++)
00362     {
00363         int row_start_index = (50 + row) * 320 + 50;
00364 
00365         interp0->base[0] = row_start_index;
00366         interp0->accum[0] = 0;
00367 
00368         for (int col = 0; col < 100; col++)
00369         {
00370             // uint32_t framebuffer_index = interp_pop_full_result(interp0, 0);
00371             printf("%d\r\n", interp_pop_full_result(interp0));
00372             // framebuffer[interp0->pop[0]] = 15; //source_region[src_index++];
00373             interp0->accum[0] += 1;
00374         }
00375     }
