04821 {
04822     FRESULT res;
04823     FATFS *fs;
04824     DWORD nfree, clst, stat;
04825     LBA_t sect;
04826     UINT i;
04827     FFOBJID obj;
04828 
04829 
04830     /* Get logical drive */
04831     res = mount_volume(&path, &fs, 0);
04832     if (res == FR_OK) {
04833         *fatfs = fs;                /* Return ptr to the fs object */
04834         /* If free_clst is valid, return it without full FAT scan */
04835         if (fs->free_clst <= fs->n_fatent - 2) {
04836             *nclst = fs->free_clst;
04837         } else {
04838             /* Scan FAT to obtain number of free clusters */
04839             nfree = 0;
04840             if (fs->fs_type == FS_FAT12) {  /* FAT12: Scan bit field FAT entries */
04841                 clst = 2; obj.fs = fs;
04842                 do {
04843                     stat = get_fat(&obj, clst);
04844                     if (stat == 0xFFFFFFFF) {
04845                         res = FR_DISK_ERR; break;
04846                     }
04847                     if (stat == 1) {
04848                         res = FR_INT_ERR; break;
04849                     }
04850                     if (stat == 0) nfree++;
04851                 } while (++clst < fs->n_fatent);
04852             } else {
04853 #if FF_FS_EXFAT␍
04854                 if (fs->fs_type == FS_EXFAT) {  /* exFAT: Scan allocation bitmap */
04855                     BYTE bm;
04856                     UINT b;
04857 
04858                     clst = fs->n_fatent - 2;    /* Number of clusters */
04859                     sect = fs->bitbase;         /* Bitmap sector */
04860                     i = 0;                      /* Offset in the sector */
04861                     do {    /* Counts numbuer of bits with zero in the bitmap */
04862                         if (i == 0) {   /* New sector? */
04863                             res = move_window(fs, sect++);
04864                             if (res != FR_OK) break;
04865                         }
04866                         for (b = 8, bm = ~fs->win[i]; b && clst; b--, clst--) {
04867                             nfree += bm & 1;
04868                             bm >>= 1;
04869                         }
04870                         i = (i + 1) % SS(fs);
04871                     } while (clst);
04872                 } else
04873 #endif␍
04874                 {   /* FAT16/32: Scan WORD/DWORD FAT entries */
04875                     clst = fs->n_fatent;    /* Number of entries */
04876                     sect = fs->fatbase;     /* Top of the FAT */
04877                     i = 0;                  /* Offset in the sector */
04878                     do {    /* Counts numbuer of entries with zero in the FAT */
04879                         if (i == 0) {   /* New sector? */
04880                             res = move_window(fs, sect++);
04881                             if (res != FR_OK) break;
04882                         }
04883                         if (fs->fs_type == FS_FAT16) {
04884                             if (ld_word(fs->win + i) == 0) nfree++;
04885                             i += 2;
04886                         } else {
04887                             if ((ld_dword(fs->win + i) & 0x0FFFFFFF) == 0) nfree++;
04888                             i += 4;
04889                         }
04890                         i %= SS(fs);
04891                     } while (--clst);
04892                 }
04893             }
04894             if (res == FR_OK) {     /* Update parameters if succeeded */
04895                 *nclst = nfree;         /* Return the free clusters */
04896                 fs->free_clst = nfree;  /* Now free_clst is valid */
04897                 fs->fsi_flag |= 1;      /* FAT32: FSInfo is to be updated */
04898             }
04899         }
04900     }
04901 
04902     LEAVE_FF(fs, res);
04903 }

04437 {
04438     FRESULT res;
04439     FATFS *fs;
04440     DWORD clst, bcs;
04441     LBA_t nsect;
04442     FSIZE_t ifptr;
04443 #if FF_USE_FASTSEEK␍
04444     DWORD cl, pcl, ncl, tcl, tlen, ulen;
04445     DWORD *tbl;
04446     LBA_t dsc;
04447 #endif␍
04448 
04449     res = validate(&fp->obj, &fs);      /* Check validity of the file object */
04450     if (res == FR_OK) res = (FRESULT)fp->err;
04451 #if FF_FS_EXFAT && !FF_FS_READONLY␍
04452     if (res == FR_OK && fs->fs_type == FS_EXFAT) {
04453         res = fill_last_frag(&fp->obj, fp->clust, 0xFFFFFFFF);  /* Fill last fragment on the FAT if needed */
04454     }
04455 #endif␍
04456     if (res != FR_OK) LEAVE_FF(fs, res);
04457 
04458 #if FF_USE_FASTSEEK␍
04459     if (fp->cltbl) {    /* Fast seek */
04460         if (ofs == CREATE_LINKMAP) {    /* Create CLMT */
04461             tbl = fp->cltbl;
04462             tlen = *tbl++; ulen = 2;    /* Given table size and required table size */
04463             cl = fp->obj.sclust;        /* Origin of the chain */
04464             if (cl != 0) {
04465                 do {
04466                     /* Get a fragment */
04467                     tcl = cl; ncl = 0; ulen += 2;   /* Top, length and used items */
04468                     do {
04469                         pcl = cl; ncl++;
04470                         cl = get_fat(&fp->obj, cl);
04471                         if (cl <= 1) ABORT(fs, FR_INT_ERR);
04472                         if (cl == 0xFFFFFFFF) ABORT(fs, FR_DISK_ERR);
04473                     } while (cl == pcl + 1);
04474                     if (ulen <= tlen) {     /* Store the length and top of the fragment */
04475                         *tbl++ = ncl; *tbl++ = tcl;
04476                     }
04477                 } while (cl < fs->n_fatent);    /* Repeat until end of chain */
04478             }
04479             *fp->cltbl = ulen;  /* Number of items used */
04480             if (ulen <= tlen) {
04481                 *tbl = 0;       /* Terminate table */
04482             } else {
04483                 res = FR_NOT_ENOUGH_CORE;   /* Given table size is smaller than required */
04484             }
04485         } else {                        /* Fast seek */
04486             if (ofs > fp->obj.objsize) ofs = fp->obj.objsize;   /* Clip offset at the file size */
04487             fp->fptr = ofs;             /* Set file pointer */
04488             if (ofs > 0) {
04489                 fp->clust = clmt_clust(fp, ofs - 1);
04490                 dsc = clst2sect(fs, fp->clust);
04491                 if (dsc == 0) ABORT(fs, FR_INT_ERR);
04492                 dsc += (DWORD)((ofs - 1) / SS(fs)) & (fs->csize - 1);
04493                 if (fp->fptr % SS(fs) && dsc != fp->sect) { /* Refill sector cache if needed */
04494 #if !FF_FS_TINY␍
04495 #if !FF_FS_READONLY␍
04496                     if (fp->flag & FA_DIRTY) {      /* Write-back dirty sector cache */
04497                         if (disk_write(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) ABORT(fs, FR_DISK_ERR);
04498                         fp->flag &= (BYTE)~FA_DIRTY;
04499                     }
04500 #endif␍
04501                     if (disk_read(fs->pdrv, fp->buf, dsc, 1) != RES_OK) ABORT(fs, FR_DISK_ERR); /* Load current sector */
04502 #endif␍
04503                     fp->sect = dsc;
04504                 }
04505             }
04506         }
04507     } else
04508 #endif␍
04509 
04510     /* Normal Seek */
04511     {
04512 #if FF_FS_EXFAT␍
04513         if (fs->fs_type != FS_EXFAT && ofs >= 0x100000000) ofs = 0xFFFFFFFF;    /* Clip at 4 GiB - 1 if at FATxx */
04514 #endif␍
04515         if (ofs > fp->obj.objsize && (FF_FS_READONLY || !(fp->flag & FA_WRITE))) {  /* In read-only mode, clip offset with the file size */
04516             ofs = fp->obj.objsize;
04517         }
04518         ifptr = fp->fptr;
04519         fp->fptr = nsect = 0;
04520         if (ofs > 0) {
04521             bcs = (DWORD)fs->csize * SS(fs);    /* Cluster size (byte) */
04522             if (ifptr > 0 &&
04523                 (ofs - 1) / bcs >= (ifptr - 1) / bcs) { /* When seek to same or following cluster, */
04524                 fp->fptr = (ifptr - 1) & ~(FSIZE_t)(bcs - 1);   /* start from the current cluster */
04525                 ofs -= fp->fptr;
04526                 clst = fp->clust;
04527             } else {                                    /* When seek to back cluster, */
04528                 clst = fp->obj.sclust;                  /* start from the first cluster */
04529 #if !FF_FS_READONLY␍
04530                 if (clst == 0) {                        /* If no cluster chain, create a new chain */
04531                     clst = create_chain(&fp->obj, 0);
04532                     if (clst == 1) ABORT(fs, FR_INT_ERR);
04533                     if (clst == 0xFFFFFFFF) ABORT(fs, FR_DISK_ERR);
04534                     fp->obj.sclust = clst;
04535                 }
04536 #endif␍
04537                 fp->clust = clst;
04538             }
04539             if (clst != 0) {
04540                 while (ofs > bcs) {                     /* Cluster following loop */
04541                     ofs -= bcs; fp->fptr += bcs;
04542 #if !FF_FS_READONLY␍
04543                     if (fp->flag & FA_WRITE) {          /* Check if in write mode or not */
04544                         if (FF_FS_EXFAT && fp->fptr > fp->obj.objsize) {    /* No FAT chain object needs correct objsize to generate FAT value */
04545                             fp->obj.objsize = fp->fptr;
04546                             fp->flag |= FA_MODIFIED;
04547                         }
04548                         clst = create_chain(&fp->obj, clst);    /* Follow chain with forceed stretch */
04549                         if (clst == 0) {                /* Clip file size in case of disk full */
04550                             ofs = 0; break;
04551                         }
04552                     } else
04553 #endif␍
04554                     {
04555                         clst = get_fat(&fp->obj, clst); /* Follow cluster chain if not in write mode */
04556                     }
04557                     if (clst == 0xFFFFFFFF) ABORT(fs, FR_DISK_ERR);
04558                     if (clst <= 1 || clst >= fs->n_fatent) ABORT(fs, FR_INT_ERR);
04559                     fp->clust = clst;
04560                 }
04561                 fp->fptr += ofs;
04562                 if (ofs % SS(fs)) {
04563                     nsect = clst2sect(fs, clst);    /* Current sector */
04564                     if (nsect == 0) ABORT(fs, FR_INT_ERR);
04565                     nsect += (DWORD)(ofs / SS(fs));
04566                 }
04567             }
04568         }
04569         if (!FF_FS_READONLY && fp->fptr > fp->obj.objsize) {    /* Set file change flag if the file size is extended */
04570             fp->obj.objsize = fp->fptr;
04571             fp->flag |= FA_MODIFIED;
04572         }
04573         if (fp->fptr % SS(fs) && nsect != fp->sect) {   /* Fill sector cache if needed */
04574 #if !FF_FS_TINY␍
04575 #if !FF_FS_READONLY␍
04576             if (fp->flag & FA_DIRTY) {          /* Write-back dirty sector cache */
04577                 if (disk_write(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) ABORT(fs, FR_DISK_ERR);
04578                 fp->flag &= (BYTE)~FA_DIRTY;
04579             }
04580 #endif␍
04581             if (disk_read(fs->pdrv, fp->buf, nsect, 1) != RES_OK) ABORT(fs, FR_DISK_ERR);   /* Fill sector cache */
04582 #endif␍
04583             fp->sect = nsect;
04584         }
04585     }
04586 
04587     LEAVE_FF(fs, res);
04588 }

05059 {
05060     FRESULT res;
05061     FATFS *fs;
05062     FATFS_DIR dj;
05063     FFOBJID sobj;
05064     DWORD dcl, pcl, tm;
05065     DEF_NAMBUF
05066 
05067 
05068     res = mount_volume(&path, &fs, FA_WRITE);   /* Get logical drive */
05069     if (res == FR_OK) {
05070         dj.obj.fs = fs;
05071         INIT_NAMBUF(fs);
05072         res = follow_path(&dj, path);           /* Follow the file path */
05073         if (res == FR_OK) res = FR_EXIST;       /* Name collision? */
05074         if (FF_FS_RPATH && res == FR_NO_FILE && (dj.fn[NSFLAG] & NS_DOT)) { /* Invalid name? */
05075             res = FR_INVALID_NAME;
05076         }
05077         if (res == FR_NO_FILE) {                /* It is clear to create a new directory */
05078             sobj.fs = fs;                       /* New object id to create a new chain */
05079             dcl = create_chain(&sobj, 0);       /* Allocate a cluster for the new directory */
05080             res = FR_OK;
05081             if (dcl == 0) res = FR_DENIED;      /* No space to allocate a new cluster? */
05082             if (dcl == 1) res = FR_INT_ERR;     /* Any insanity? */
05083             if (dcl == 0xFFFFFFFF) res = FR_DISK_ERR;   /* Disk error? */
05084             tm = GET_FATTIME();
05085             if (res == FR_OK) {
05086                 res = dir_clear(fs, dcl);       /* Clean up the new table */
05087                 if (res == FR_OK) {
05088                     if (!FF_FS_EXFAT || fs->fs_type != FS_EXFAT) {  /* Create dot entries (FAT only) */
05089                         memset(fs->win + DIR_Name, ' ', 11);    /* Create "." entry */
05090                         fs->win[DIR_Name] = '.';
05091                         fs->win[DIR_Attr] = AM_DIR;
05092                         st_dword(fs->win + DIR_ModTime, tm);
05093                         st_clust(fs, fs->win, dcl);
05094                         memcpy(fs->win + SZDIRE, fs->win, SZDIRE);  /* Create ".." entry */
05095                         fs->win[SZDIRE + 1] = '.'; pcl = dj.obj.sclust;
05096                         st_clust(fs, fs->win + SZDIRE, pcl);
05097                         fs->wflag = 1;
05098                     }
05099                     res = dir_register(&dj);    /* Register the object to the parent directoy */
05100                 }
05101             }
05102             if (res == FR_OK) {
05103 #if FF_FS_EXFAT␍
05104                 if (fs->fs_type == FS_EXFAT) {  /* Initialize directory entry block */
05105                     st_dword(fs->dirbuf + XDIR_ModTime, tm);    /* Created time */
05106                     st_dword(fs->dirbuf + XDIR_FstClus, dcl);   /* Table start cluster */
05107                     st_dword(fs->dirbuf + XDIR_FileSize, (DWORD)fs->csize * SS(fs));    /* Directory size needs to be valid */
05108                     st_dword(fs->dirbuf + XDIR_ValidFileSize, (DWORD)fs->csize * SS(fs));
05109                     fs->dirbuf[XDIR_GenFlags] = 3;              /* Initialize the object flag */
05110                     fs->dirbuf[XDIR_Attr] = AM_DIR;             /* Attribute */
05111                     res = store_xdir(&dj);
05112                 } else
05113 #endif␍
05114                 {
05115                     st_dword(dj.dir + DIR_ModTime, tm); /* Created time */
05116                     st_clust(fs, dj.dir, dcl);          /* Table start cluster */
05117                     dj.dir[DIR_Attr] = AM_DIR;          /* Attribute */
05118                     fs->wflag = 1;
05119                 }
05120                 if (res == FR_OK) {
05121                     res = sync_fs(fs);
05122                 }
05123             } else {
05124                 remove_chain(&sobj, dcl, 0);        /* Could not register, remove the allocated cluster */
05125             }
05126         }
05127         FREE_NAMBUF();
05128     }
05129 
05130     LEAVE_FF(fs, res);
05131 }

05888 {
05889     static const WORD cst[] = {1, 4, 16, 64, 256, 512, 0};  /* Cluster size boundary for FAT volume (4Ks unit) */
05890     static const WORD cst32[] = {1, 2, 4, 8, 16, 32, 0};    /* Cluster size boundary for FAT32 volume (128Ks unit) */
05891     static const MKFS_PARM defopt = {FM_ANY, 0, 0, 0, 0};   /* Default parameter */
05892     BYTE fsopt, fsty, sys, pdrv, ipart;
05893     BYTE *buf;
05894     BYTE *pte;
05895     WORD ss;    /* Sector size */
05896     DWORD sz_buf, sz_blk, n_clst, pau, nsect, n, vsn;
05897     LBA_t sz_vol, b_vol, b_fat, b_data;     /* Size of volume, Base LBA of volume, fat, data */
05898     LBA_t sect, lba[2];
05899     DWORD sz_rsv, sz_fat, sz_dir, sz_au;    /* Size of reserved, fat, dir, data, cluster */
05900     UINT n_fat, n_root, i;                  /* Index, Number of FATs and Number of roor dir entries */
05901     int vol;
05902     DSTATUS ds;
05903     FRESULT res;
05904 
05905 
05906     /* Check mounted drive and clear work area */
05907     vol = get_ldnumber(&path);                  /* Get target logical drive */
05908     if (vol < 0) return FR_INVALID_DRIVE;
05909     if (FatFs[vol]) FatFs[vol]->fs_type = 0;    /* Clear the fs object if mounted */
05910     pdrv = LD2PD(vol);      /* Hosting physical drive */
05911     ipart = LD2PT(vol);     /* Hosting partition (0:create as new, 1..:existing partition) */
05912 
05913     /* Initialize the hosting physical drive */
05914     ds = disk_initialize(pdrv);
05915     if (ds & STA_NOINIT) return FR_NOT_READY;
05916     if (ds & STA_PROTECT) return FR_WRITE_PROTECTED;
05917 
05918     /* Get physical drive parameters (sz_drv, sz_blk and ss) */
05919     if (!opt) opt = &defopt;    /* Use default parameter if it is not given */
05920     sz_blk = opt->align;
05921     if (sz_blk == 0) disk_ioctl(pdrv, GET_BLOCK_SIZE, &sz_blk);                 /* Block size from the paramter or lower layer */
05922     if (sz_blk == 0 || sz_blk > 0x8000 || (sz_blk & (sz_blk - 1))) sz_blk = 1;  /* Use default if the block size is invalid */
05923 #if FF_MAX_SS != FF_MIN_SS␍
05924     if (disk_ioctl(pdrv, GET_SECTOR_SIZE, &ss) != RES_OK) return FR_DISK_ERR;
05925     if (ss > FF_MAX_SS || ss < FF_MIN_SS || (ss & (ss - 1))) return FR_DISK_ERR;
05926 #else␍
05927     ss = FF_MAX_SS;
05928 #endif␍
05929 
05930     /* Options for FAT sub-type and FAT parameters */
05931     fsopt = opt->fmt & (FM_ANY | FM_SFD);
05932     n_fat = (opt->n_fat >= 1 && opt->n_fat <= 2) ? opt->n_fat : 1;
05933     n_root = (opt->n_root >= 1 && opt->n_root <= 32768 && (opt->n_root % (ss / SZDIRE)) == 0) ? opt->n_root : 512;
05934     sz_au = (opt->au_size <= 0x1000000 && (opt->au_size & (opt->au_size - 1)) == 0) ? opt->au_size : 0;
05935     sz_au /= ss;    /* Byte --> Sector */
05936 
05937     /* Get working buffer */
05938     sz_buf = len / ss;      /* Size of working buffer [sector] */
05939     if (sz_buf == 0) return FR_NOT_ENOUGH_CORE;
05940     buf = (BYTE*)work;      /* Working buffer */
05941 #if FF_USE_LFN == 3␍
05942     if (!buf) buf = ff_memalloc(sz_buf * ss);   /* Use heap memory for working buffer */
05943 #endif␍
05944     if (!buf) return FR_NOT_ENOUGH_CORE;
05945 
05946     /* Determine where the volume to be located (b_vol, sz_vol) */
05947     b_vol = sz_vol = 0;
05948     if (FF_MULTI_PARTITION && ipart != 0) { /* Is the volume associated with any specific partition? */
05949         /* Get partition location from the existing partition table */
05950         if (disk_read(pdrv, buf, 0, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);  /* Load MBR */
05951         if (ld_word(buf + BS_55AA) != 0xAA55) LEAVE_MKFS(FR_MKFS_ABORTED);  /* Check if MBR is valid */
05952 #if FF_LBA64␍
05953         if (buf[MBR_Table + PTE_System] == 0xEE) {  /* GPT protective MBR? */
05954             DWORD n_ent, ofs;
05955             QWORD pt_lba;
05956 
05957             /* Get the partition location from GPT */
05958             if (disk_read(pdrv, buf, 1, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);  /* Load GPT header sector (next to MBR) */
05959             if (!test_gpt_header(buf)) LEAVE_MKFS(FR_MKFS_ABORTED); /* Check if GPT header is valid */
05960             n_ent = ld_dword(buf + GPTH_PtNum);     /* Number of entries */
05961             pt_lba = ld_qword(buf + GPTH_PtOfs);    /* Table start sector */
05962             ofs = i = 0;
05963             while (n_ent) {     /* Find MS Basic partition with order of ipart */
05964                 if (ofs == 0 && disk_read(pdrv, buf, pt_lba++, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);   /* Get PT sector */
05965                 if (!memcmp(buf + ofs + GPTE_PtGuid, GUID_MS_Basic, 16) && ++i == ipart) {  /* MS basic data partition? */
05966                     b_vol = ld_qword(buf + ofs + GPTE_FstLba);
05967                     sz_vol = ld_qword(buf + ofs + GPTE_LstLba) - b_vol + 1;
05968                     break;
05969                 }
05970                 n_ent--; ofs = (ofs + SZ_GPTE) % ss;    /* Next entry */
05971             }
05972             if (n_ent == 0) LEAVE_MKFS(FR_MKFS_ABORTED);    /* Partition not found */
05973             fsopt |= 0x80;  /* Partitioning is in GPT */
05974         } else
05975 #endif␍
05976         {   /* Get the partition location from MBR partition table */
05977             pte = buf + (MBR_Table + (ipart - 1) * SZ_PTE);
05978             if (ipart > 4 || pte[PTE_System] == 0) LEAVE_MKFS(FR_MKFS_ABORTED); /* No partition? */
05979             b_vol = ld_dword(pte + PTE_StLba);      /* Get volume start sector */
05980             sz_vol = ld_dword(pte + PTE_SizLba);    /* Get volume size */
05981         }
05982     } else {    /* The volume is associated with a physical drive */
05983         if (disk_ioctl(pdrv, GET_SECTOR_COUNT, &sz_vol) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
05984         if (!(fsopt & FM_SFD)) {    /* To be partitioned? */
05985             /* Create a single-partition on the drive in this function */
05986 #if FF_LBA64␍
05987             if (sz_vol >= FF_MIN_GPT) { /* Which partition type to create, MBR or GPT? */
05988                 fsopt |= 0x80;      /* Partitioning is in GPT */
05989                 b_vol = GPT_ALIGN / ss; sz_vol -= b_vol + GPT_ITEMS * SZ_GPTE / ss + 1; /* Estimated partition offset and size */
05990             } else
05991 #endif␍
05992             {   /* Partitioning is in MBR */
05993                 if (sz_vol > N_SEC_TRACK) {
05994                     b_vol = N_SEC_TRACK; sz_vol -= b_vol;   /* Estimated partition offset and size */
05995                 }
05996             }
05997         }
05998     }
05999     if (sz_vol < 128) LEAVE_MKFS(FR_MKFS_ABORTED);  /* Check if volume size is >=128s */
06000 
06001     /* Now start to create an FAT volume at b_vol and sz_vol */
06002 
06003     do {    /* Pre-determine the FAT type */
06004         if (FF_FS_EXFAT && (fsopt & FM_EXFAT)) {    /* exFAT possible? */
06005             if ((fsopt & FM_ANY) == FM_EXFAT || sz_vol >= 0x4000000 || sz_au > 128) {   /* exFAT only, vol >= 64MS or sz_au > 128S ? */
06006                 fsty = FS_EXFAT; break;
06007             }
06008         }
06009 #if FF_LBA64␍
06010         if (sz_vol >= 0x100000000) LEAVE_MKFS(FR_MKFS_ABORTED); /* Too large volume for FAT/FAT32 */
06011 #endif␍
06012         if (sz_au > 128) sz_au = 128;   /* Invalid AU for FAT/FAT32? */
06013         if (fsopt & FM_FAT32) { /* FAT32 possible? */
06014             if (!(fsopt & FM_FAT)) {    /* no-FAT? */
06015                 fsty = FS_FAT32; break;
06016             }
06017         }
06018         if (!(fsopt & FM_FAT)) LEAVE_MKFS(FR_INVALID_PARAMETER);    /* no-FAT? */
06019         fsty = FS_FAT16;
06020     } while (0);
06021 
06022     vsn = (DWORD)sz_vol + GET_FATTIME();    /* VSN generated from current time and partitiion size */
06023 
06024 #if FF_FS_EXFAT␍
06025     if (fsty == FS_EXFAT) { /* Create an exFAT volume */
06026         DWORD szb_bit, szb_case, sum, nbit, clu, clen[3];
06027         WCHAR ch, si;
06028         UINT j, st;
06029 
06030         if (sz_vol < 0x1000) LEAVE_MKFS(FR_MKFS_ABORTED);   /* Too small volume for exFAT? */
06031 #if FF_USE_TRIM␍
06032         lba[0] = b_vol; lba[1] = b_vol + sz_vol - 1;    /* Inform storage device that the volume area may be erased */
06033         disk_ioctl(pdrv, CTRL_TRIM, lba);
06034 #endif␍
06035         /* Determine FAT location, data location and number of clusters */
06036         if (sz_au == 0) {   /* AU auto-selection */
06037             sz_au = 8;
06038             if (sz_vol >= 0x80000) sz_au = 64;      /* >= 512Ks */
06039             if (sz_vol >= 0x4000000) sz_au = 256;   /* >= 64Ms */
06040         }
06041         b_fat = b_vol + 32;                                     /* FAT start at offset 32 */
06042         sz_fat = (DWORD)((sz_vol / sz_au + 2) * 4 + ss - 1) / ss;   /* Number of FAT sectors */
06043         b_data = (b_fat + sz_fat + sz_blk - 1) & ~((LBA_t)sz_blk - 1);  /* Align data area to the erase block boundary */
06044         if (b_data - b_vol >= sz_vol / 2) LEAVE_MKFS(FR_MKFS_ABORTED);  /* Too small volume? */
06045         n_clst = (DWORD)((sz_vol - (b_data - b_vol)) / sz_au);  /* Number of clusters */
06046         if (n_clst <16) LEAVE_MKFS(FR_MKFS_ABORTED);            /* Too few clusters? */
06047         if (n_clst > MAX_EXFAT) LEAVE_MKFS(FR_MKFS_ABORTED);    /* Too many clusters? */
06048 
06049         szb_bit = (n_clst + 7) / 8;                             /* Size of allocation bitmap */
06050         clen[0] = (szb_bit + sz_au * ss - 1) / (sz_au * ss);    /* Number of allocation bitmap clusters */
06051 
06052         /* Create a compressed up-case table */
06053         sect = b_data + sz_au * clen[0];    /* Table start sector */
06054         sum = 0;                            /* Table checksum to be stored in the 82 entry */
06055         st = 0; si = 0; i = 0; j = 0; szb_case = 0;
06056         do {
06057             switch (st) {
06058             case 0:
06059                 ch = (WCHAR)ff_wtoupper(si);    /* Get an up-case char */
06060                 if (ch != si) {
06061                     si++; break;        /* Store the up-case char if exist */
06062                 }
06063                 for (j = 1; (WCHAR)(si + j) && (WCHAR)(si + j) == ff_wtoupper((WCHAR)(si + j)); j++) ;  /* Get run length of no-case block */
06064                 if (j >= 128) {
06065                     ch = 0xFFFF; st = 2; break; /* Compress the no-case block if run is >= 128 chars */
06066                 }
06067                 st = 1;         /* Do not compress short run */
06068                 /* FALLTHROUGH */
06069             case 1:
06070                 ch = si++;      /* Fill the short run */
06071                 if (--j == 0) st = 0;
06072                 break;
06073 
06074             default:
06075                 ch = (WCHAR)j; si += (WCHAR)j;  /* Number of chars to skip */
06076                 st = 0;
06077             }
06078             sum = xsum32(buf[i + 0] = (BYTE)ch, sum);   /* Put it into the write buffer */
06079             sum = xsum32(buf[i + 1] = (BYTE)(ch >> 8), sum);
06080             i += 2; szb_case += 2;
06081             if (si == 0 || i == sz_buf * ss) {      /* Write buffered data when buffer full or end of process */
06082                 n = (i + ss - 1) / ss;
06083                 if (disk_write(pdrv, buf, sect, n) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06084                 sect += n; i = 0;
06085             }
06086         } while (si);
06087         clen[1] = (szb_case + sz_au * ss - 1) / (sz_au * ss);   /* Number of up-case table clusters */
06088         clen[2] = 1;    /* Number of root dir clusters */
06089 
06090         /* Initialize the allocation bitmap */
06091         sect = b_data; nsect = (szb_bit + ss - 1) / ss; /* Start of bitmap and number of bitmap sectors */
06092         nbit = clen[0] + clen[1] + clen[2];             /* Number of clusters in-use by system (bitmap, up-case and root-dir) */
06093         do {
06094             memset(buf, 0, sz_buf * ss);                /* Initialize bitmap buffer */
06095             for (i = 0; nbit != 0 && i / 8 < sz_buf * ss; buf[i / 8] |= 1 << (i % 8), i++, nbit--) ;    /* Mark used clusters */
06096             n = (nsect > sz_buf) ? sz_buf : nsect;      /* Write the buffered data */
06097             if (disk_write(pdrv, buf, sect, n) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06098             sect += n; nsect -= n;
06099         } while (nsect);
06100 
06101         /* Initialize the FAT */
06102         sect = b_fat; nsect = sz_fat;   /* Start of FAT and number of FAT sectors */
06103         j = nbit = clu = 0;
06104         do {
06105             memset(buf, 0, sz_buf * ss); i = 0; /* Clear work area and reset write offset */
06106             if (clu == 0) { /* Initialize FAT [0] and FAT[1] */
06107                 st_dword(buf + i, 0xFFFFFFF8); i += 4; clu++;
06108                 st_dword(buf + i, 0xFFFFFFFF); i += 4; clu++;
06109             }
06110             do {            /* Create chains of bitmap, up-case and root dir */
06111                 while (nbit != 0 && i < sz_buf * ss) {  /* Create a chain */
06112                     st_dword(buf + i, (nbit > 1) ? clu + 1 : 0xFFFFFFFF);
06113                     i += 4; clu++; nbit--;
06114                 }
06115                 if (nbit == 0 && j < 3) nbit = clen[j++];   /* Get next chain length */
06116             } while (nbit != 0 && i < sz_buf * ss);
06117             n = (nsect > sz_buf) ? sz_buf : nsect;  /* Write the buffered data */
06118             if (disk_write(pdrv, buf, sect, n) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06119             sect += n; nsect -= n;
06120         } while (nsect);
06121 
06122         /* Initialize the root directory */
06123         memset(buf, 0, sz_buf * ss);
06124         buf[SZDIRE * 0 + 0] = ET_VLABEL;                /* Volume label entry (no label) */
06125         buf[SZDIRE * 1 + 0] = ET_BITMAP;                /* Bitmap entry */
06126         st_dword(buf + SZDIRE * 1 + 20, 2);             /*  cluster */
06127         st_dword(buf + SZDIRE * 1 + 24, szb_bit);       /*  size */
06128         buf[SZDIRE * 2 + 0] = ET_UPCASE;                /* Up-case table entry */
06129         st_dword(buf + SZDIRE * 2 + 4, sum);            /*  sum */
06130         st_dword(buf + SZDIRE * 2 + 20, 2 + clen[0]);   /*  cluster */
06131         st_dword(buf + SZDIRE * 2 + 24, szb_case);      /*  size */
06132         sect = b_data + sz_au * (clen[0] + clen[1]); nsect = sz_au; /* Start of the root directory and number of sectors */
06133         do {    /* Fill root directory sectors */
06134             n = (nsect > sz_buf) ? sz_buf : nsect;
06135             if (disk_write(pdrv, buf, sect, n) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06136             memset(buf, 0, ss); /* Rest of entries are filled with zero */
06137             sect += n; nsect -= n;
06138         } while (nsect);
06139 
06140         /* Create two set of the exFAT VBR blocks */
06141         sect = b_vol;
06142         for (n = 0; n < 2; n++) {
06143             /* Main record (+0) */
06144             memset(buf, 0, ss);
06145             memcpy(buf + BS_JmpBoot, "\xEB\x76\x90" "EXFAT   ", 11);    /* Boot jump code (x86), OEM name */
06146             st_qword(buf + BPB_VolOfsEx, b_vol);                    /* Volume offset in the physical drive [sector] */
06147             st_qword(buf + BPB_TotSecEx, sz_vol);                   /* Volume size [sector] */
06148             st_dword(buf + BPB_FatOfsEx, (DWORD)(b_fat - b_vol));   /* FAT offset [sector] */
06149             st_dword(buf + BPB_FatSzEx, sz_fat);                    /* FAT size [sector] */
06150             st_dword(buf + BPB_DataOfsEx, (DWORD)(b_data - b_vol)); /* Data offset [sector] */
06151             st_dword(buf + BPB_NumClusEx, n_clst);                  /* Number of clusters */
06152             st_dword(buf + BPB_RootClusEx, 2 + clen[0] + clen[1]);  /* Root dir cluster # */
06153             st_dword(buf + BPB_VolIDEx, vsn);                       /* VSN */
06154             st_word(buf + BPB_FSVerEx, 0x100);                      /* Filesystem version (1.00) */
06155             for (buf[BPB_BytsPerSecEx] = 0, i = ss; i >>= 1; buf[BPB_BytsPerSecEx]++) ; /* Log2 of sector size [byte] */
06156             for (buf[BPB_SecPerClusEx] = 0, i = sz_au; i >>= 1; buf[BPB_SecPerClusEx]++) ;  /* Log2 of cluster size [sector] */
06157             buf[BPB_NumFATsEx] = 1;                 /* Number of FATs */
06158             buf[BPB_DrvNumEx] = 0x80;               /* Drive number (for int13) */
06159             st_word(buf + BS_BootCodeEx, 0xFEEB);   /* Boot code (x86) */
06160             st_word(buf + BS_55AA, 0xAA55);         /* Signature (placed here regardless of sector size) */
06161             for (i = sum = 0; i < ss; i++) {        /* VBR checksum */
06162                 if (i != BPB_VolFlagEx && i != BPB_VolFlagEx + 1 && i != BPB_PercInUseEx) sum = xsum32(buf[i], sum);
06163             }
06164             if (disk_write(pdrv, buf, sect++, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06165             /* Extended bootstrap record (+1..+8) */
06166             memset(buf, 0, ss);
06167             st_word(buf + ss - 2, 0xAA55);  /* Signature (placed at end of sector) */
06168             for (j = 1; j < 9; j++) {
06169                 for (i = 0; i < ss; sum = xsum32(buf[i++], sum)) ;  /* VBR checksum */
06170                 if (disk_write(pdrv, buf, sect++, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06171             }
06172             /* OEM/Reserved record (+9..+10) */
06173             memset(buf, 0, ss);
06174             for ( ; j < 11; j++) {
06175                 for (i = 0; i < ss; sum = xsum32(buf[i++], sum)) ;  /* VBR checksum */
06176                 if (disk_write(pdrv, buf, sect++, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06177             }
06178             /* Sum record (+11) */
06179             for (i = 0; i < ss; i += 4) st_dword(buf + i, sum);     /* Fill with checksum value */
06180             if (disk_write(pdrv, buf, sect++, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06181         }
06182 
06183     } else
06184 #endif  /* FF_FS_EXFAT */
06185     {   /* Create an FAT/FAT32 volume */
06186         do {
06187             pau = sz_au;
06188             /* Pre-determine number of clusters and FAT sub-type */
06189             if (fsty == FS_FAT32) { /* FAT32 volume */
06190                 if (pau == 0) { /* AU auto-selection */
06191                     n = (DWORD)sz_vol / 0x20000;    /* Volume size in unit of 128KS */
06192                     for (i = 0, pau = 1; cst32[i] && cst32[i] <= n; i++, pau <<= 1) ;   /* Get from table */
06193                 }
06194                 n_clst = (DWORD)sz_vol / pau;   /* Number of clusters */
06195                 sz_fat = (n_clst * 4 + 8 + ss - 1) / ss;    /* FAT size [sector] */
06196                 sz_rsv = 32;    /* Number of reserved sectors */
06197                 sz_dir = 0;     /* No static directory */
06198                 if (n_clst <= MAX_FAT16 || n_clst > MAX_FAT32) LEAVE_MKFS(FR_MKFS_ABORTED);
06199             } else {                /* FAT volume */
06200                 if (pau == 0) { /* au auto-selection */
06201                     n = (DWORD)sz_vol / 0x1000; /* Volume size in unit of 4KS */
06202                     for (i = 0, pau = 1; cst[i] && cst[i] <= n; i++, pau <<= 1) ;   /* Get from table */
06203                 }
06204                 n_clst = (DWORD)sz_vol / pau;
06205                 if (n_clst > MAX_FAT12) {
06206                     n = n_clst * 2 + 4;     /* FAT size [byte] */
06207                 } else {
06208                     fsty = FS_FAT12;
06209                     n = (n_clst * 3 + 1) / 2 + 3;   /* FAT size [byte] */
06210                 }
06211                 sz_fat = (n + ss - 1) / ss;     /* FAT size [sector] */
06212                 sz_rsv = 1;                     /* Number of reserved sectors */
06213                 sz_dir = (DWORD)n_root * SZDIRE / ss;   /* Root dir size [sector] */
06214             }
06215             b_fat = b_vol + sz_rsv;                     /* FAT base */
06216             b_data = b_fat + sz_fat * n_fat + sz_dir;   /* Data base */
06217 
06218             /* Align data area to erase block boundary (for flash memory media) */
06219             n = (DWORD)(((b_data + sz_blk - 1) & ~(sz_blk - 1)) - b_data);  /* Sectors to next nearest from current data base */
06220             if (fsty == FS_FAT32) {     /* FAT32: Move FAT */
06221                 sz_rsv += n; b_fat += n;
06222             } else {                    /* FAT: Expand FAT */
06223                 if (n % n_fat) {    /* Adjust fractional error if needed */
06224                     n--; sz_rsv++; b_fat++;
06225                 }
06226                 sz_fat += n / n_fat;
06227             }
06228 
06229             /* Determine number of clusters and final check of validity of the FAT sub-type */
06230             if (sz_vol < b_data + pau * 16 - b_vol) LEAVE_MKFS(FR_MKFS_ABORTED);    /* Too small volume? */
06231             n_clst = ((DWORD)sz_vol - sz_rsv - sz_fat * n_fat - sz_dir) / pau;
06232             if (fsty == FS_FAT32) {
06233                 if (n_clst <= MAX_FAT16) {  /* Too few clusters for FAT32? */
06234                     if (sz_au == 0 && (sz_au = pau / 2) != 0) continue; /* Adjust cluster size and retry */
06235                     LEAVE_MKFS(FR_MKFS_ABORTED);
06236                 }
06237             }
06238             if (fsty == FS_FAT16) {
06239                 if (n_clst > MAX_FAT16) {   /* Too many clusters for FAT16 */
06240                     if (sz_au == 0 && (pau * 2) <= 64) {
06241                         sz_au = pau * 2; continue;  /* Adjust cluster size and retry */
06242                     }
06243                     if ((fsopt & FM_FAT32)) {
06244                         fsty = FS_FAT32; continue;  /* Switch type to FAT32 and retry */
06245                     }
06246                     if (sz_au == 0 && (sz_au = pau * 2) <= 128) continue;   /* Adjust cluster size and retry */
06247                     LEAVE_MKFS(FR_MKFS_ABORTED);
06248                 }
06249                 if  (n_clst <= MAX_FAT12) { /* Too few clusters for FAT16 */
06250                     if (sz_au == 0 && (sz_au = pau * 2) <= 128) continue;   /* Adjust cluster size and retry */
06251                     LEAVE_MKFS(FR_MKFS_ABORTED);
06252                 }
06253             }
06254             if (fsty == FS_FAT12 && n_clst > MAX_FAT12) LEAVE_MKFS(FR_MKFS_ABORTED);    /* Too many clusters for FAT12 */
06255 
06256             /* Ok, it is the valid cluster configuration */
06257             break;
06258         } while (1);
06259 
06260 #if FF_USE_TRIM␍
06261         lba[0] = b_vol; lba[1] = b_vol + sz_vol - 1;    /* Inform storage device that the volume area may be erased */
06262         disk_ioctl(pdrv, CTRL_TRIM, lba);
06263 #endif␍
06264         /* Create FAT VBR */
06265         memset(buf, 0, ss);
06266         memcpy(buf + BS_JmpBoot, "\xEB\xFE\x90" "MSDOS5.0", 11);    /* Boot jump code (x86), OEM name */
06267         st_word(buf + BPB_BytsPerSec, ss);              /* Sector size [byte] */
06268         buf[BPB_SecPerClus] = (BYTE)pau;                /* Cluster size [sector] */
06269         st_word(buf + BPB_RsvdSecCnt, (WORD)sz_rsv);    /* Size of reserved area */
06270         buf[BPB_NumFATs] = (BYTE)n_fat;                 /* Number of FATs */
06271         st_word(buf + BPB_RootEntCnt, (WORD)((fsty == FS_FAT32) ? 0 : n_root)); /* Number of root directory entries */
06272         if (sz_vol < 0x10000) {
06273             st_word(buf + BPB_TotSec16, (WORD)sz_vol);  /* Volume size in 16-bit LBA */
06274         } else {
06275             st_dword(buf + BPB_TotSec32, (DWORD)sz_vol);    /* Volume size in 32-bit LBA */
06276         }
06277         buf[BPB_Media] = 0xF8;                          /* Media descriptor byte */
06278         st_word(buf + BPB_SecPerTrk, 63);               /* Number of sectors per track (for int13) */
06279         st_word(buf + BPB_NumHeads, 255);               /* Number of heads (for int13) */
06280         st_dword(buf + BPB_HiddSec, (DWORD)b_vol);      /* Volume offset in the physical drive [sector] */
06281         if (fsty == FS_FAT32) {
06282             st_dword(buf + BS_VolID32, vsn);            /* VSN */
06283             st_dword(buf + BPB_FATSz32, sz_fat);        /* FAT size [sector] */
06284             st_dword(buf + BPB_RootClus32, 2);          /* Root directory cluster # (2) */
06285             st_word(buf + BPB_FSInfo32, 1);             /* Offset of FSINFO sector (VBR + 1) */
06286             st_word(buf + BPB_BkBootSec32, 6);          /* Offset of backup VBR (VBR + 6) */
06287             buf[BS_DrvNum32] = 0x80;                    /* Drive number (for int13) */
06288             buf[BS_BootSig32] = 0x29;                   /* Extended boot signature */
06289             memcpy(buf + BS_VolLab32, "NO NAME    " "FAT32   ", 19);    /* Volume label, FAT signature */
06290         } else {
06291             st_dword(buf + BS_VolID, vsn);              /* VSN */
06292             st_word(buf + BPB_FATSz16, (WORD)sz_fat);   /* FAT size [sector] */
06293             buf[BS_DrvNum] = 0x80;                      /* Drive number (for int13) */
06294             buf[BS_BootSig] = 0x29;                     /* Extended boot signature */
06295             memcpy(buf + BS_VolLab, "NO NAME    " "FAT     ", 19);  /* Volume label, FAT signature */
06296         }
06297         st_word(buf + BS_55AA, 0xAA55);                 /* Signature (offset is fixed here regardless of sector size) */
06298         if (disk_write(pdrv, buf, b_vol, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR); /* Write it to the VBR sector */
06299 
06300         /* Create FSINFO record if needed */
06301         if (fsty == FS_FAT32) {
06302             disk_write(pdrv, buf, b_vol + 6, 1);        /* Write backup VBR (VBR + 6) */
06303             memset(buf, 0, ss);
06304             st_dword(buf + FSI_LeadSig, 0x41615252);
06305             st_dword(buf + FSI_StrucSig, 0x61417272);
06306             st_dword(buf + FSI_Free_Count, n_clst - 1); /* Number of free clusters */
06307             st_dword(buf + FSI_Nxt_Free, 2);            /* Last allocated cluster# */
06308             st_word(buf + BS_55AA, 0xAA55);
06309             disk_write(pdrv, buf, b_vol + 7, 1);        /* Write backup FSINFO (VBR + 7) */
06310             disk_write(pdrv, buf, b_vol + 1, 1);        /* Write original FSINFO (VBR + 1) */
06311         }
06312 
06313         /* Initialize FAT area */
06314         memset(buf, 0, sz_buf * ss);
06315         sect = b_fat;       /* FAT start sector */
06316         for (i = 0; i < n_fat; i++) {           /* Initialize FATs each */
06317             if (fsty == FS_FAT32) {
06318                 st_dword(buf + 0, 0xFFFFFFF8);  /* FAT[0] */
06319                 st_dword(buf + 4, 0xFFFFFFFF);  /* FAT[1] */
06320                 st_dword(buf + 8, 0x0FFFFFFF);  /* FAT[2] (root directory) */
06321             } else {
06322                 st_dword(buf + 0, (fsty == FS_FAT12) ? 0xFFFFF8 : 0xFFFFFFF8);  /* FAT[0] and FAT[1] */
06323             }
06324             nsect = sz_fat;     /* Number of FAT sectors */
06325             do {    /* Fill FAT sectors */
06326                 n = (nsect > sz_buf) ? sz_buf : nsect;
06327                 if (disk_write(pdrv, buf, sect, (UINT)n) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06328                 memset(buf, 0, ss); /* Rest of FAT all are cleared */
06329                 sect += n; nsect -= n;
06330             } while (nsect);
06331         }
06332 
06333         /* Initialize root directory (fill with zero) */
06334         nsect = (fsty == FS_FAT32) ? pau : sz_dir;  /* Number of root directory sectors */
06335         do {
06336             n = (nsect > sz_buf) ? sz_buf : nsect;
06337             if (disk_write(pdrv, buf, sect, (UINT)n) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06338             sect += n; nsect -= n;
06339         } while (nsect);
06340     }
06341 
06342     /* A FAT volume has been created here */
06343 
06344     /* Determine system ID in the MBR partition table */
06345     if (FF_FS_EXFAT && fsty == FS_EXFAT) {
06346         sys = 0x07;     /* exFAT */
06347     } else if (fsty == FS_FAT32) {
06348         sys = 0x0C;     /* FAT32X */
06349     } else if (sz_vol >= 0x10000) {
06350         sys = 0x06;     /* FAT12/16 (large) */
06351     } else if (fsty == FS_FAT16) {
06352         sys = 0x04;     /* FAT16 */
06353     } else {
06354         sys = 0x01;     /* FAT12 */
06355     }
06356 
06357     /* Update partition information */
06358     if (FF_MULTI_PARTITION && ipart != 0) { /* Volume is in the existing partition */
06359         if (!FF_LBA64 || !(fsopt & 0x80)) { /* Is the partition in MBR? */
06360             /* Update system ID in the partition table */
06361             if (disk_read(pdrv, buf, 0, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);  /* Read the MBR */
06362             buf[MBR_Table + (ipart - 1) * SZ_PTE + PTE_System] = sys;           /* Set system ID */
06363             if (disk_write(pdrv, buf, 0, 1) != RES_OK) LEAVE_MKFS(FR_DISK_ERR); /* Write it back to the MBR */
06364         }
06365     } else {                                /* Volume as a new single partition */
06366         if (!(fsopt & FM_SFD)) {            /* Create partition table if not in SFD format */
06367             lba[0] = sz_vol; lba[1] = 0;
06368             res = create_partition(pdrv, lba, sys, buf);
06369             if (res != FR_OK) LEAVE_MKFS(res);
06370         }
06371     }
06372 
06373     if (disk_ioctl(pdrv, CTRL_SYNC, 0) != RES_OK) LEAVE_MKFS(FR_DISK_ERR);
06374 
06375     LEAVE_MKFS(FR_OK);
06376 }

03662 {
03663     FATFS *cfs;
03664     int vol;
03665     FRESULT res;
03666     const TCHAR *rp = path;
03667 
03668 
03669     /* Get volume ID (logical drive number) */
03670     vol = get_ldnumber(&rp);
03671     if (vol < 0) return FR_INVALID_DRIVE;
03672     cfs = FatFs[vol];           /* Pointer to the filesystem object of the volume */
03673 
03674     if (cfs) {                  /* Unregister current filesystem object if regsitered */
03675         FatFs[vol] = 0;
03676 #if FF_FS_LOCK␍
03677         clear_share(cfs);
03678 #endif␍
03679 #if FF_FS_REENTRANT             /* Discard mutex of the current volume */
03680         ff_mutex_delete(vol);
03681 #endif␍
03682         cfs->fs_type = 0;       /* Invalidate the filesystem object to be unregistered */
03683     }
03684 
03685     if (fs) {                   /* Register new filesystem object */
03686         fs->pdrv = LD2PD(vol);  /* Volume hosting physical drive */
03687 #if FF_FS_REENTRANT             /* Create a volume mutex */
03688         fs->ldrv = (BYTE)vol;   /* Owner volume ID */
03689         if (!ff_mutex_create(vol)) return FR_INT_ERR;
03690 #if FF_FS_LOCK␍
03691         if (SysLock == 0) {     /* Create a system mutex if needed */
03692             if (!ff_mutex_create(FF_VOLUMES)) {
03693                 ff_mutex_delete(vol);
03694                 return FR_INT_ERR;
03695             }
03696             SysLock = 1;        /* System mutex is ready */
03697         }
03698 #endif␍
03699 #endif␍
03700         fs->fs_type = 0;        /* Invalidate the new filesystem object */
03701         FatFs[vol] = fs;        /* Register new fs object */
03702     }
03703 
03704     if (opt == 0) return FR_OK; /* Do not mount now, it will be mounted in subsequent file functions */
03705 
03706     res = mount_volume(&path, &fs, 0);  /* Force mounted the volume */
03707     LEAVE_FF(fs, res);
03708 }

03722 {
03723     FRESULT res;
03724     FATFS_DIR dj;
03725     FATFS *fs;
03726 #if !FF_FS_READONLY␍
03727     DWORD cl, bcs, clst, tm;
03728     LBA_t sc;
03729     FSIZE_t ofs;
03730 #endif␍
03731     DEF_NAMBUF
03732 
03733 
03734     if (!fp) return FR_INVALID_OBJECT;
03735 
03736     /* Get logical drive number */
03737     mode &= FF_FS_READONLY ? FA_READ : FA_READ | FA_WRITE | FA_CREATE_ALWAYS | FA_CREATE_NEW | FA_OPEN_ALWAYS | FA_OPEN_APPEND;
03738     res = mount_volume(&path, &fs, mode);
03739     if (res == FR_OK) {
03740         dj.obj.fs = fs;
03741         INIT_NAMBUF(fs);
03742         res = follow_path(&dj, path);   /* Follow the file path */
03743 #if !FF_FS_READONLY /* Read/Write configuration */
03744         if (res == FR_OK) {
03745             if (dj.fn[NSFLAG] & NS_NONAME) {    /* Origin directory itself? */
03746                 res = FR_INVALID_NAME;
03747             }
03748 #if FF_FS_LOCK␍
03749             else {
03750                 res = chk_share(&dj, (mode & ~FA_READ) ? 1 : 0);    /* Check if the file can be used */
03751             }
03752 #endif␍
03753         }
03754         /* Create or Open a file */
03755         if (mode & (FA_CREATE_ALWAYS | FA_OPEN_ALWAYS | FA_CREATE_NEW)) {
03756             if (res != FR_OK) {                 /* No file, create new */
03757                 if (res == FR_NO_FILE) {        /* There is no file to open, create a new entry */
03758 #if FF_FS_LOCK␍
03759                     res = enq_share() ? dir_register(&dj) : FR_TOO_MANY_OPEN_FILES;
03760 #else␍
03761                     res = dir_register(&dj);
03762 #endif␍
03763                 }
03764                 mode |= FA_CREATE_ALWAYS;       /* File is created */
03765             }
03766             else {                              /* Any object with the same name is already existing */
03767                 if (dj.obj.attr & (AM_RDO | AM_DIR)) {  /* Cannot overwrite it (R/O or DIR) */
03768                     res = FR_DENIED;
03769                 } else {
03770                     if (mode & FA_CREATE_NEW) res = FR_EXIST;   /* Cannot create as new file */
03771                 }
03772             }
03773             if (res == FR_OK && (mode & FA_CREATE_ALWAYS)) {    /* Truncate the file if overwrite mode */
03774 #if FF_FS_EXFAT␍
03775                 if (fs->fs_type == FS_EXFAT) {
03776                     /* Get current allocation info */
03777                     fp->obj.fs = fs;
03778                     init_alloc_info(fs, &fp->obj);
03779                     /* Set directory entry block initial state */
03780                     memset(fs->dirbuf + 2, 0, 30);  /* Clear 85 entry except for NumSec */
03781                     memset(fs->dirbuf + 38, 0, 26); /* Clear C0 entry except for NumName and NameHash */
03782                     fs->dirbuf[XDIR_Attr] = AM_ARC;
03783                     st_dword(fs->dirbuf + XDIR_CrtTime, GET_FATTIME());
03784                     fs->dirbuf[XDIR_GenFlags] = 1;
03785                     res = store_xdir(&dj);
03786                     if (res == FR_OK && fp->obj.sclust != 0) {  /* Remove the cluster chain if exist */
03787                         res = remove_chain(&fp->obj, fp->obj.sclust, 0);
03788                         fs->last_clst = fp->obj.sclust - 1;     /* Reuse the cluster hole */
03789                     }
03790                 } else
03791 #endif␍
03792                 {
03793                     /* Set directory entry initial state */
03794                     tm = GET_FATTIME();                 /* Set created time */
03795                     st_dword(dj.dir + DIR_CrtTime, tm);
03796                     st_dword(dj.dir + DIR_ModTime, tm);
03797                     cl = ld_clust(fs, dj.dir);          /* Get current cluster chain */
03798                     dj.dir[DIR_Attr] = AM_ARC;          /* Reset attribute */
03799                     st_clust(fs, dj.dir, 0);            /* Reset file allocation info */
03800                     st_dword(dj.dir + DIR_FileSize, 0);
03801                     fs->wflag = 1;
03802                     if (cl != 0) {                      /* Remove the cluster chain if exist */
03803                         sc = fs->winsect;
03804                         res = remove_chain(&dj.obj, cl, 0);
03805                         if (res == FR_OK) {
03806                             res = move_window(fs, sc);
03807                             fs->last_clst = cl - 1;     /* Reuse the cluster hole */
03808                         }
03809                     }
03810                 }
03811             }
03812         }
03813         else {  /* Open an existing file */
03814             if (res == FR_OK) {                 /* Is the object exsiting? */
03815                 if (dj.obj.attr & AM_DIR) {     /* File open against a directory */
03816                     res = FR_NO_FILE;
03817                 } else {
03818                     if ((mode & FA_WRITE) && (dj.obj.attr & AM_RDO)) { /* Write mode open against R/O file */
03819                         res = FR_DENIED;
03820                     }
03821                 }
03822             }
03823         }
03824         if (res == FR_OK) {
03825             if (mode & FA_CREATE_ALWAYS) mode |= FA_MODIFIED;   /* Set file change flag if created or overwritten */
03826             fp->dir_sect = fs->winsect;         /* Pointer to the directory entry */
03827             fp->dir_ptr = dj.dir;
03828 #if FF_FS_LOCK␍
03829             fp->obj.lockid = inc_share(&dj, (mode & ~FA_READ) ? 1 : 0); /* Lock the file for this session */
03830             if (fp->obj.lockid == 0) res = FR_INT_ERR;
03831 #endif␍
03832         }
03833 #else       /* R/O configuration */
03834         if (res == FR_OK) {
03835             if (dj.fn[NSFLAG] & NS_NONAME) {    /* Is it origin directory itself? */
03836                 res = FR_INVALID_NAME;
03837             } else {
03838                 if (dj.obj.attr & AM_DIR) {     /* Is it a directory? */
03839                     res = FR_NO_FILE;
03840                 }
03841             }
03842         }
03843 #endif␍
03844 
03845         if (res == FR_OK) {
03846 #if FF_FS_EXFAT␍
03847             if (fs->fs_type == FS_EXFAT) {
03848                 fp->obj.c_scl = dj.obj.sclust;                          /* Get containing directory info */
03849                 fp->obj.c_size = ((DWORD)dj.obj.objsize & 0xFFFFFF00) | dj.obj.stat;
03850                 fp->obj.c_ofs = dj.blk_ofs;
03851                 init_alloc_info(fs, &fp->obj);
03852             } else
03853 #endif␍
03854             {
03855                 fp->obj.sclust = ld_clust(fs, dj.dir);                  /* Get object allocation info */
03856                 fp->obj.objsize = ld_dword(dj.dir + DIR_FileSize);
03857             }
03858 #if FF_USE_FASTSEEK␍
03859             fp->cltbl = 0;      /* Disable fast seek mode */
03860 #endif␍
03861             fp->obj.fs = fs;    /* Validate the file object */
03862             fp->obj.id = fs->id;
03863             fp->flag = mode;    /* Set file access mode */
03864             fp->err = 0;        /* Clear error flag */
03865             fp->sect = 0;       /* Invalidate current data sector */
03866             fp->fptr = 0;       /* Set file pointer top of the file */
03867 #if !FF_FS_READONLY␍
03868 #if !FF_FS_TINY␍
03869             memset(fp->buf, 0, sizeof fp->buf); /* Clear sector buffer */
03870 #endif␍
03871             if ((mode & FA_SEEKEND) && fp->obj.objsize > 0) {   /* Seek to end of file if FA_OPEN_APPEND is specified */
03872                 fp->fptr = fp->obj.objsize;         /* Offset to seek */
03873                 bcs = (DWORD)fs->csize * SS(fs);    /* Cluster size in byte */
03874                 clst = fp->obj.sclust;              /* Follow the cluster chain */
03875                 for (ofs = fp->obj.objsize; res == FR_OK && ofs > bcs; ofs -= bcs) {
03876                     clst = get_fat(&fp->obj, clst);
03877                     if (clst <= 1) res = FR_INT_ERR;
03878                     if (clst == 0xFFFFFFFF) res = FR_DISK_ERR;
03879                 }
03880                 fp->clust = clst;
03881                 if (res == FR_OK && ofs % SS(fs)) { /* Fill sector buffer if not on the sector boundary */
03882                     sc = clst2sect(fs, clst);
03883                     if (sc == 0) {
03884                         res = FR_INT_ERR;
03885                     } else {
03886                         fp->sect = sc + (DWORD)(ofs / SS(fs));
03887 #if !FF_FS_TINY␍
03888                         if (disk_read(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) res = FR_DISK_ERR;
03889 #endif␍
03890                     }
03891                 }
03892 #if FF_FS_LOCK␍
03893                 if (res != FR_OK) dec_share(fp->obj.lockid); /* Decrement file open counter if seek failed */
03894 #endif␍
03895             }
03896 #endif␍
03897         }
03898 
03899         FREE_NAMBUF();
03900     }
03901 
03902     if (res != FR_OK) fp->obj.fs = 0;   /* Invalidate file object on error */
03903 
03904     LEAVE_FF(fs, res);
03905 }

04601 {
04602     FRESULT res;
04603     FATFS *fs;
04604     DEF_NAMBUF
04605 
04606 
04607     if (!dp) return FR_INVALID_OBJECT;
04608 
04609     /* Get logical drive */
04610     res = mount_volume(&path, &fs, 0);
04611     if (res == FR_OK) {
04612         dp->obj.fs = fs;
04613         INIT_NAMBUF(fs);
04614         res = follow_path(dp, path);            /* Follow the path to the directory */
04615         if (res == FR_OK) {                     /* Follow completed */
04616             if (!(dp->fn[NSFLAG] & NS_NONAME)) {    /* It is not the origin directory itself */
04617                 if (dp->obj.attr & AM_DIR) {        /* This object is a sub-directory */
04618 #if FF_FS_EXFAT␍
04619                     if (fs->fs_type == FS_EXFAT) {
04620                         dp->obj.c_scl = dp->obj.sclust; /* Get containing directory inforamation */
04621                         dp->obj.c_size = ((DWORD)dp->obj.objsize & 0xFFFFFF00) | dp->obj.stat;
04622                         dp->obj.c_ofs = dp->blk_ofs;
04623                         init_alloc_info(fs, &dp->obj);  /* Get object allocation info */
04624                     } else
04625 #endif␍
04626                     {
04627                         dp->obj.sclust = ld_clust(fs, dp->dir); /* Get object allocation info */
04628                     }
04629                 } else {                        /* This object is a file */
04630                     res = FR_NO_PATH;
04631                 }
04632             }
04633             if (res == FR_OK) {
04634                 dp->obj.id = fs->id;
04635                 res = dir_sdi(dp, 0);           /* Rewind directory */
04636 #if FF_FS_LOCK␍
04637                 if (res == FR_OK) {
04638                     if (dp->obj.sclust != 0) {
04639                         dp->obj.lockid = inc_share(dp, 0);  /* Lock the sub directory */
04640                         if (!dp->obj.lockid) res = FR_TOO_MANY_OPEN_FILES;
04641                     } else {
04642                         dp->obj.lockid = 0; /* Root directory need not to be locked */
04643                     }
04644                 }
04645 #endif␍
04646             }
04647         }
04648         FREE_NAMBUF();
04649         if (res == FR_NO_FILE) res = FR_NO_PATH;
04650     }
04651     if (res != FR_OK) dp->obj.fs = 0;       /* Invalidate the directory object if function failed */
04652 
04653     LEAVE_FF(fs, res);
04654 }

03920 {
03921     FRESULT res;
03922     FATFS *fs;
03923     DWORD clst;
03924     LBA_t sect;
03925     FSIZE_t remain;
03926     UINT rcnt, cc, csect;
03927     BYTE *rbuff = (BYTE*)buff;
03928 
03929 
03930     *br = 0;    /* Clear read byte counter */
03931     res = validate(&fp->obj, &fs);              /* Check validity of the file object */
03932     if (res != FR_OK || (res = (FRESULT)fp->err) != FR_OK) LEAVE_FF(fs, res);   /* Check validity */
03933     if (!(fp->flag & FA_READ)) LEAVE_FF(fs, FR_DENIED); /* Check access mode */
03934     remain = fp->obj.objsize - fp->fptr;
03935     if (btr > remain) btr = (UINT)remain;       /* Truncate btr by remaining bytes */
03936 
03937     for ( ; btr > 0; btr -= rcnt, *br += rcnt, rbuff += rcnt, fp->fptr += rcnt) {   /* Repeat until btr bytes read */
03938         if (fp->fptr % SS(fs) == 0) {           /* On the sector boundary? */
03939             csect = (UINT)(fp->fptr / SS(fs) & (fs->csize - 1));    /* Sector offset in the cluster */
03940             if (csect == 0) {                   /* On the cluster boundary? */
03941                 if (fp->fptr == 0) {            /* On the top of the file? */
03942                     clst = fp->obj.sclust;      /* Follow cluster chain from the origin */
03943                 } else {                        /* Middle or end of the file */
03944 #if FF_USE_FASTSEEK␍
03945                     if (fp->cltbl) {
03946                         clst = clmt_clust(fp, fp->fptr);    /* Get cluster# from the CLMT */
03947                     } else
03948 #endif␍
03949                     {
03950                         clst = get_fat(&fp->obj, fp->clust);    /* Follow cluster chain on the FAT */
03951                     }
03952                 }
03953                 if (clst < 2) ABORT(fs, FR_INT_ERR);
03954                 if (clst == 0xFFFFFFFF) ABORT(fs, FR_DISK_ERR);
03955                 fp->clust = clst;               /* Update current cluster */
03956             }
03957             sect = clst2sect(fs, fp->clust);    /* Get current sector */
03958             if (sect == 0) ABORT(fs, FR_INT_ERR);
03959             sect += csect;
03960             cc = btr / SS(fs);                  /* When remaining bytes >= sector size, */
03961             if (cc > 0) {                       /* Read maximum contiguous sectors directly */
03962                 if (csect + cc > fs->csize) {   /* Clip at cluster boundary */
03963                     cc = fs->csize - csect;
03964                 }
03965                 if (disk_read(fs->pdrv, rbuff, sect, cc) != RES_OK) ABORT(fs, FR_DISK_ERR);
03966 #if !FF_FS_READONLY && FF_FS_MINIMIZE <= 2      /* Replace one of the read sectors with cached data if it contains a dirty sector */
03967 #if FF_FS_TINY␍
03968                 if (fs->wflag && fs->winsect - sect < cc) {
03969                     memcpy(rbuff + ((fs->winsect - sect) * SS(fs)), fs->win, SS(fs));
03970                 }
03971 #else␍
03972                 if ((fp->flag & FA_DIRTY) && fp->sect - sect < cc) {
03973                     memcpy(rbuff + ((fp->sect - sect) * SS(fs)), fp->buf, SS(fs));
03974                 }
03975 #endif␍
03976 #endif␍
03977                 rcnt = SS(fs) * cc;             /* Number of bytes transferred */
03978                 continue;
03979             }
03980 #if !FF_FS_TINY␍
03981             if (fp->sect != sect) {         /* Load data sector if not in cache */
03982 #if !FF_FS_READONLY␍
03983                 if (fp->flag & FA_DIRTY) {      /* Write-back dirty sector cache */
03984                     if (disk_write(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) ABORT(fs, FR_DISK_ERR);
03985                     fp->flag &= (BYTE)~FA_DIRTY;
03986                 }
03987 #endif␍
03988                 if (disk_read(fs->pdrv, fp->buf, sect, 1) != RES_OK) ABORT(fs, FR_DISK_ERR);    /* Fill sector cache */
03989             }
03990 #endif␍
03991             fp->sect = sect;
03992         }
03993         rcnt = SS(fs) - (UINT)fp->fptr % SS(fs);    /* Number of bytes remains in the sector */
03994         if (rcnt > btr) rcnt = btr;                 /* Clip it by btr if needed */
03995 #if FF_FS_TINY␍
03996         if (move_window(fs, fp->sect) != FR_OK) ABORT(fs, FR_DISK_ERR); /* Move sector window */
03997         memcpy(rbuff, fs->win + fp->fptr % SS(fs), rcnt);   /* Extract partial sector */
03998 #else␍
03999         memcpy(rbuff, fp->buf + fp->fptr % SS(fs), rcnt);   /* Extract partial sector */
04000 #endif␍
04001     }
04002 
04003     LEAVE_FF(fs, FR_OK);
04004 }

04697 {
04698     FRESULT res;
04699     FATFS *fs;
04700     DEF_NAMBUF
04701 
04702 
04703     res = validate(&dp->obj, &fs);  /* Check validity of the directory object */
04704     if (res == FR_OK) {
04705         if (!fno) {
04706             res = dir_sdi(dp, 0);       /* Rewind the directory object */
04707         } else {
04708             INIT_NAMBUF(fs);
04709             res = DIR_READ_FILE(dp);        /* Read an item */
04710             if (res == FR_NO_FILE) res = FR_OK; /* Ignore end of directory */
04711             if (res == FR_OK) {             /* A valid entry is found */
04712                 get_fileinfo(dp, fno);      /* Get the object information */
04713                 res = dir_next(dp, 0);      /* Increment index for next */
04714                 if (res == FR_NO_FILE) res = FR_OK; /* Ignore end of directory now */
04715             }
04716             FREE_NAMBUF();
04717         }
04718     }
04719     LEAVE_FF(fs, res);
04720 }

05144 {
05145     FRESULT res;
05146     FATFS *fs;
05147     FATFS_DIR djo, djn;
05148     BYTE buf[FF_FS_EXFAT ? SZDIRE * 2 : SZDIRE], *dir;
05149     LBA_t sect;
05150     DEF_NAMBUF
05151 
05152 
05153     get_ldnumber(&path_new);                        /* Snip the drive number of new name off */
05154     res = mount_volume(&path_old, &fs, FA_WRITE);   /* Get logical drive of the old object */
05155     if (res == FR_OK) {
05156         djo.obj.fs = fs;
05157         INIT_NAMBUF(fs);
05158         res = follow_path(&djo, path_old);          /* Check old object */
05159         if (res == FR_OK && (djo.fn[NSFLAG] & (NS_DOT | NS_NONAME))) res = FR_INVALID_NAME; /* Check validity of name */
05160 #if FF_FS_LOCK␍
05161         if (res == FR_OK) {
05162             res = chk_share(&djo, 2);
05163         }
05164 #endif␍
05165         if (res == FR_OK) {                 /* Object to be renamed is found */
05166 #if FF_FS_EXFAT␍
05167             if (fs->fs_type == FS_EXFAT) {  /* At exFAT volume */
05168                 BYTE nf, nn;
05169                 WORD nh;
05170 
05171                 memcpy(buf, fs->dirbuf, SZDIRE * 2);    /* Save 85+C0 entry of old object */
05172                 memcpy(&djn, &djo, sizeof djo);
05173                 res = follow_path(&djn, path_new);      /* Make sure if new object name is not in use */
05174                 if (res == FR_OK) {                     /* Is new name already in use by any other object? */
05175                     res = (djn.obj.sclust == djo.obj.sclust && djn.dptr == djo.dptr) ? FR_NO_FILE : FR_EXIST;
05176                 }
05177                 if (res == FR_NO_FILE) {                /* It is a valid path and no name collision */
05178                     res = dir_register(&djn);           /* Register the new entry */
05179                     if (res == FR_OK) {
05180                         nf = fs->dirbuf[XDIR_NumSec]; nn = fs->dirbuf[XDIR_NumName];
05181                         nh = ld_word(fs->dirbuf + XDIR_NameHash);
05182                         memcpy(fs->dirbuf, buf, SZDIRE * 2);    /* Restore 85+C0 entry */
05183                         fs->dirbuf[XDIR_NumSec] = nf; fs->dirbuf[XDIR_NumName] = nn;
05184                         st_word(fs->dirbuf + XDIR_NameHash, nh);
05185                         if (!(fs->dirbuf[XDIR_Attr] & AM_DIR)) fs->dirbuf[XDIR_Attr] |= AM_ARC; /* Set archive attribute if it is a file */
05186 /* Start of critical section where an interruption can cause a cross-link */
05187                         res = store_xdir(&djn);
05188                     }
05189                 }
05190             } else
05191 #endif␍
05192             {   /* At FAT/FAT32 volume */
05193                 memcpy(buf, djo.dir, SZDIRE);           /* Save directory entry of the object */
05194                 memcpy(&djn, &djo, sizeof (FATFS_DIR));     /* Duplicate the directory object */
05195                 res = follow_path(&djn, path_new);      /* Make sure if new object name is not in use */
05196                 if (res == FR_OK) {                     /* Is new name already in use by any other object? */
05197                     res = (djn.obj.sclust == djo.obj.sclust && djn.dptr == djo.dptr) ? FR_NO_FILE : FR_EXIST;
05198                 }
05199                 if (res == FR_NO_FILE) {                /* It is a valid path and no name collision */
05200                     res = dir_register(&djn);           /* Register the new entry */
05201                     if (res == FR_OK) {
05202                         dir = djn.dir;                  /* Copy directory entry of the object except name */
05203                         memcpy(dir + 13, buf + 13, SZDIRE - 13);
05204                         dir[DIR_Attr] = buf[DIR_Attr];
05205                         if (!(dir[DIR_Attr] & AM_DIR)) dir[DIR_Attr] |= AM_ARC; /* Set archive attribute if it is a file */
05206                         fs->wflag = 1;
05207                         if ((dir[DIR_Attr] & AM_DIR) && djo.obj.sclust != djn.obj.sclust) { /* Update .. entry in the sub-directory if needed */
05208                             sect = clst2sect(fs, ld_clust(fs, dir));
05209                             if (sect == 0) {
05210                                 res = FR_INT_ERR;
05211                             } else {
05212 /* Start of critical section where an interruption can cause a cross-link */
05213                                 res = move_window(fs, sect);
05214                                 dir = fs->win + SZDIRE * 1; /* Ptr to .. entry */
05215                                 if (res == FR_OK && dir[1] == '.') {
05216                                     st_clust(fs, dir, djn.obj.sclust);
05217                                     fs->wflag = 1;
05218                                 }
05219                             }
05220                         }
05221                     }
05222                 }
05223             }
05224             if (res == FR_OK) {
05225                 res = dir_remove(&djo);     /* Remove old entry */
05226                 if (res == FR_OK) {
05227                     res = sync_fs(fs);
05228                 }
05229             }
05230 /* End of the critical section */
05231         }
05232         FREE_NAMBUF();
05233     }
05234 
05235     LEAVE_FF(fs, res);
05236 }

04785 {
04786     FRESULT res;
04787     FATFS_DIR dj;
04788     DEF_NAMBUF
04789 
04790 
04791     /* Get logical drive */
04792     res = mount_volume(&path, &dj.obj.fs, 0);
04793     if (res == FR_OK) {
04794         INIT_NAMBUF(dj.obj.fs);
04795         res = follow_path(&dj, path);   /* Follow the file path */
04796         if (res == FR_OK) {             /* Follow completed */
04797             if (dj.fn[NSFLAG] & NS_NONAME) {    /* It is origin directory */
04798                 res = FR_INVALID_NAME;
04799             } else {                            /* Found an object */
04800                 if (fno) get_fileinfo(&dj, fno);
04801             }
04802         }
04803         FREE_NAMBUF();
04804     }
04805 
04806     LEAVE_FF(dj.obj.fs, res);
04807 }

04138 {
04139     FRESULT res;
04140     FATFS *fs;
04141     DWORD tm;
04142     BYTE *dir;
04143 
04144 
04145     res = validate(&fp->obj, &fs);  /* Check validity of the file object */
04146     if (res == FR_OK) {
04147         if (fp->flag & FA_MODIFIED) {   /* Is there any change to the file? */
04148 #if !FF_FS_TINY␍
04149             if (fp->flag & FA_DIRTY) {  /* Write-back cached data if needed */
04150                 if (disk_write(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) LEAVE_FF(fs, FR_DISK_ERR);
04151                 fp->flag &= (BYTE)~FA_DIRTY;
04152             }
04153 #endif␍
04154             /* Update the directory entry */
04155             tm = GET_FATTIME();             /* Modified time */
04156 #if FF_FS_EXFAT␍
04157             if (fs->fs_type == FS_EXFAT) {
04158                 res = fill_first_frag(&fp->obj);    /* Fill first fragment on the FAT if needed */
04159                 if (res == FR_OK) {
04160                     res = fill_last_frag(&fp->obj, fp->clust, 0xFFFFFFFF);  /* Fill last fragment on the FAT if needed */
04161                 }
04162                 if (res == FR_OK) {
04163                     FATFS_DIR dj;
04164                     DEF_NAMBUF
04165 
04166                     INIT_NAMBUF(fs);
04167                     res = load_obj_xdir(&dj, &fp->obj); /* Load directory entry block */
04168                     if (res == FR_OK) {
04169                         fs->dirbuf[XDIR_Attr] |= AM_ARC;                /* Set archive attribute to indicate that the file has been changed */
04170                         fs->dirbuf[XDIR_GenFlags] = fp->obj.stat | 1;   /* Update file allocation information */
04171                         st_dword(fs->dirbuf + XDIR_FstClus, fp->obj.sclust);        /* Update start cluster */
04172                         st_qword(fs->dirbuf + XDIR_FileSize, fp->obj.objsize);      /* Update file size */
04173                         st_qword(fs->dirbuf + XDIR_ValidFileSize, fp->obj.objsize); /* (FatFs does not support Valid File Size feature) */
04174                         st_dword(fs->dirbuf + XDIR_ModTime, tm);        /* Update modified time */
04175                         fs->dirbuf[XDIR_ModTime10] = 0;
04176                         st_dword(fs->dirbuf + XDIR_AccTime, 0);
04177                         res = store_xdir(&dj);  /* Restore it to the directory */
04178                         if (res == FR_OK) {
04179                             res = sync_fs(fs);
04180                             fp->flag &= (BYTE)~FA_MODIFIED;
04181                         }
04182                     }
04183                     FREE_NAMBUF();
04184                 }
04185             } else
04186 #endif␍
04187             {
04188                 res = move_window(fs, fp->dir_sect);
04189                 if (res == FR_OK) {
04190                     dir = fp->dir_ptr;
04191                     dir[DIR_Attr] |= AM_ARC;                        /* Set archive attribute to indicate that the file has been changed */
04192                     st_clust(fp->obj.fs, dir, fp->obj.sclust);      /* Update file allocation information  */
04193                     st_dword(dir + DIR_FileSize, (DWORD)fp->obj.objsize);   /* Update file size */
04194                     st_dword(dir + DIR_ModTime, tm);                /* Update modified time */
04195                     st_word(dir + DIR_LstAccDate, 0);
04196                     fs->wflag = 1;
04197                     res = sync_fs(fs);                  /* Restore it to the directory */
04198                     fp->flag &= (BYTE)~FA_MODIFIED;
04199                 }
04200             }
04201         }
04202     }
04203 
04204     LEAVE_FF(fs, res);
04205 }

04915 {
04916     FRESULT res;
04917     FATFS *fs;
04918     DWORD ncl;
04919 
04920 
04921     res = validate(&fp->obj, &fs);  /* Check validity of the file object */
04922     if (res != FR_OK || (res = (FRESULT)fp->err) != FR_OK) LEAVE_FF(fs, res);
04923     if (!(fp->flag & FA_WRITE)) LEAVE_FF(fs, FR_DENIED);    /* Check access mode */
04924 
04925     if (fp->fptr < fp->obj.objsize) {   /* Process when fptr is not on the eof */
04926         if (fp->fptr == 0) {    /* When set file size to zero, remove entire cluster chain */
04927             res = remove_chain(&fp->obj, fp->obj.sclust, 0);
04928             fp->obj.sclust = 0;
04929         } else {                /* When truncate a part of the file, remove remaining clusters */
04930             ncl = get_fat(&fp->obj, fp->clust);
04931             res = FR_OK;
04932             if (ncl == 0xFFFFFFFF) res = FR_DISK_ERR;
04933             if (ncl == 1) res = FR_INT_ERR;
04934             if (res == FR_OK && ncl < fs->n_fatent) {
04935                 res = remove_chain(&fp->obj, ncl, fp->clust);
04936             }
04937         }
04938         fp->obj.objsize = fp->fptr; /* Set file size to current read/write point */
04939         fp->flag |= FA_MODIFIED;
04940 #if !FF_FS_TINY␍
04941         if (res == FR_OK && (fp->flag & FA_DIRTY)) {
04942             if (disk_write(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) {
04943                 res = FR_DISK_ERR;
04944             } else {
04945                 fp->flag &= (BYTE)~FA_DIRTY;
04946             }
04947         }
04948 #endif␍
04949         if (res != FR_OK) ABORT(fs, res);
04950     }
04951 
04952     LEAVE_FF(fs, res);
04953 }

04965 {
04966     FRESULT res;
04967     FATFS *fs;
04968     FATFS_DIR dj, sdj;
04969     DWORD dclst = 0;
04970 #if FF_FS_EXFAT␍
04971     FFOBJID obj;
04972 #endif␍
04973     DEF_NAMBUF
04974 
04975 
04976     /* Get logical drive */
04977     res = mount_volume(&path, &fs, FA_WRITE);
04978     if (res == FR_OK) {
04979         dj.obj.fs = fs;
04980         INIT_NAMBUF(fs);
04981         res = follow_path(&dj, path);       /* Follow the file path */
04982         if (FF_FS_RPATH && res == FR_OK && (dj.fn[NSFLAG] & NS_DOT)) {
04983             res = FR_INVALID_NAME;          /* Cannot remove dot entry */
04984         }
04985 #if FF_FS_LOCK␍
04986         if (res == FR_OK) res = chk_share(&dj, 2);  /* Check if it is an open object */
04987 #endif␍
04988         if (res == FR_OK) {                 /* The object is accessible */
04989             if (dj.fn[NSFLAG] & NS_NONAME) {
04990                 res = FR_INVALID_NAME;      /* Cannot remove the origin directory */
04991             } else {
04992                 if (dj.obj.attr & AM_RDO) {
04993                     res = FR_DENIED;        /* Cannot remove R/O object */
04994                 }
04995             }
04996             if (res == FR_OK) {
04997 #if FF_FS_EXFAT␍
04998                 obj.fs = fs;
04999                 if (fs->fs_type == FS_EXFAT) {
05000                     init_alloc_info(fs, &obj);
05001                     dclst = obj.sclust;
05002                 } else
05003 #endif␍
05004                 {
05005                     dclst = ld_clust(fs, dj.dir);
05006                 }
05007                 if (dj.obj.attr & AM_DIR) {         /* Is it a sub-directory? */
05008 #if FF_FS_RPATH != 0␍
05009                     if (dclst == fs->cdir) {        /* Is it the current directory? */
05010                         res = FR_DENIED;
05011                     } else
05012 #endif␍
05013                     {
05014                         sdj.obj.fs = fs;            /* Open the sub-directory */
05015                         sdj.obj.sclust = dclst;
05016 #if FF_FS_EXFAT␍
05017                         if (fs->fs_type == FS_EXFAT) {
05018                             sdj.obj.objsize = obj.objsize;
05019                             sdj.obj.stat = obj.stat;
05020                         }
05021 #endif␍
05022                         res = dir_sdi(&sdj, 0);
05023                         if (res == FR_OK) {
05024                             res = DIR_READ_FILE(&sdj);          /* Test if the directory is empty */
05025                             if (res == FR_OK) res = FR_DENIED;  /* Not empty? */
05026                             if (res == FR_NO_FILE) res = FR_OK; /* Empty? */
05027                         }
05028                     }
05029                 }
05030             }
05031             if (res == FR_OK) {
05032                 res = dir_remove(&dj);          /* Remove the directory entry */
05033                 if (res == FR_OK && dclst != 0) {   /* Remove the cluster chain if exist */
05034 #if FF_FS_EXFAT␍
05035                     res = remove_chain(&obj, dclst, 0);
05036 #else␍
05037                     res = remove_chain(&dj.obj, dclst, 0);
05038 #endif␍
05039                 }
05040                 if (res == FR_OK) res = sync_fs(fs);
05041             }
05042         }
05043         FREE_NAMBUF();
05044     }
05045 
05046     LEAVE_FF(fs, res);
05047 }

04020 {
04021     FRESULT res;
04022     FATFS *fs;
04023     DWORD clst;
04024     LBA_t sect;
04025     UINT wcnt, cc, csect;
04026     const BYTE *wbuff = (const BYTE*)buff;
04027 
04028 
04029     *bw = 0;    /* Clear write byte counter */
04030     res = validate(&fp->obj, &fs);          /* Check validity of the file object */
04031     if (res != FR_OK || (res = (FRESULT)fp->err) != FR_OK) LEAVE_FF(fs, res);   /* Check validity */
04032     if (!(fp->flag & FA_WRITE)) LEAVE_FF(fs, FR_DENIED);    /* Check access mode */
04033 
04034     /* Check fptr wrap-around (file size cannot reach 4 GiB at FAT volume) */
04035     if ((!FF_FS_EXFAT || fs->fs_type != FS_EXFAT) && (DWORD)(fp->fptr + btw) < (DWORD)fp->fptr) {
04036         btw = (UINT)(0xFFFFFFFF - (DWORD)fp->fptr);
04037     }
04038 
04039     for ( ; btw > 0; btw -= wcnt, *bw += wcnt, wbuff += wcnt, fp->fptr += wcnt, fp->obj.objsize = (fp->fptr > fp->obj.objsize) ? fp->fptr : fp->obj.objsize) {  /* Repeat until all data written */
04040         if (fp->fptr % SS(fs) == 0) {       /* On the sector boundary? */
04041             csect = (UINT)(fp->fptr / SS(fs)) & (fs->csize - 1);    /* Sector offset in the cluster */
04042             if (csect == 0) {               /* On the cluster boundary? */
04043                 if (fp->fptr == 0) {        /* On the top of the file? */
04044                     clst = fp->obj.sclust;  /* Follow from the origin */
04045                     if (clst == 0) {        /* If no cluster is allocated, */
04046                         clst = create_chain(&fp->obj, 0);   /* create a new cluster chain */
04047                     }
04048                 } else {                    /* On the middle or end of the file */
04049 #if FF_USE_FASTSEEK␍
04050                     if (fp->cltbl) {
04051                         clst = clmt_clust(fp, fp->fptr);    /* Get cluster# from the CLMT */
04052                     } else
04053 #endif␍
04054                     {
04055                         clst = create_chain(&fp->obj, fp->clust);   /* Follow or stretch cluster chain on the FAT */
04056                     }
04057                 }
04058                 if (clst == 0) break;       /* Could not allocate a new cluster (disk full) */
04059                 if (clst == 1) ABORT(fs, FR_INT_ERR);
04060                 if (clst == 0xFFFFFFFF) ABORT(fs, FR_DISK_ERR);
04061                 fp->clust = clst;           /* Update current cluster */
04062                 if (fp->obj.sclust == 0) fp->obj.sclust = clst; /* Set start cluster if the first write */
04063             }
04064 #if FF_FS_TINY␍
04065             if (fs->winsect == fp->sect && sync_window(fs) != FR_OK) ABORT(fs, FR_DISK_ERR);    /* Write-back sector cache */
04066 #else␍
04067             if (fp->flag & FA_DIRTY) {      /* Write-back sector cache */
04068                 if (disk_write(fs->pdrv, fp->buf, fp->sect, 1) != RES_OK) ABORT(fs, FR_DISK_ERR);
04069                 fp->flag &= (BYTE)~FA_DIRTY;
04070             }
04071 #endif␍
04072             sect = clst2sect(fs, fp->clust);    /* Get current sector */
04073             if (sect == 0) ABORT(fs, FR_INT_ERR);
04074             sect += csect;
04075             cc = btw / SS(fs);              /* When remaining bytes >= sector size, */
04076             if (cc > 0) {                   /* Write maximum contiguous sectors directly */
04077                 if (csect + cc > fs->csize) {   /* Clip at cluster boundary */
04078                     cc = fs->csize - csect;
04079                 }
04080                 if (disk_write(fs->pdrv, wbuff, sect, cc) != RES_OK) ABORT(fs, FR_DISK_ERR);
04081 #if FF_FS_MINIMIZE <= 2␍
04082 #if FF_FS_TINY␍
04083                 if (fs->winsect - sect < cc) {  /* Refill sector cache if it gets invalidated by the direct write */
04084                     memcpy(fs->win, wbuff + ((fs->winsect - sect) * SS(fs)), SS(fs));
04085                     fs->wflag = 0;
04086                 }
04087 #else␍
04088                 if (fp->sect - sect < cc) { /* Refill sector cache if it gets invalidated by the direct write */
04089                     memcpy(fp->buf, wbuff + ((fp->sect - sect) * SS(fs)), SS(fs));
04090                     fp->flag &= (BYTE)~FA_DIRTY;
04091                 }
04092 #endif␍
04093 #endif␍
04094                 wcnt = SS(fs) * cc;     /* Number of bytes transferred */
04095                 continue;
04096             }
04097 #if FF_FS_TINY␍
04098             if (fp->fptr >= fp->obj.objsize) {  /* Avoid silly cache filling on the growing edge */
04099                 if (sync_window(fs) != FR_OK) ABORT(fs, FR_DISK_ERR);
04100                 fs->winsect = sect;
04101             }
04102 #else␍
04103             if (fp->sect != sect &&         /* Fill sector cache with file data */
04104                 fp->fptr < fp->obj.objsize &&
04105                 disk_read(fs->pdrv, fp->buf, sect, 1) != RES_OK) {
04106                     ABORT(fs, FR_DISK_ERR);
04107             }
04108 #endif␍
04109             fp->sect = sect;
04110         }
04111         wcnt = SS(fs) - (UINT)fp->fptr % SS(fs);    /* Number of bytes remains in the sector */
04112         if (wcnt > btw) wcnt = btw;                 /* Clip it by btw if needed */
04113 #if FF_FS_TINY␍
04114         if (move_window(fs, fp->sect) != FR_OK) ABORT(fs, FR_DISK_ERR); /* Move sector window */
04115         memcpy(fs->win + fp->fptr % SS(fs), wbuff, wcnt);   /* Fit data to the sector */
04116         fs->wflag = 1;
04117 #else␍
04118         memcpy(fp->buf + fp->fptr % SS(fs), wbuff, wcnt);   /* Fit data to the sector */
04119         fp->flag |= FA_DIRTY;
04120 #endif␍
04121     }
04122 
04123     fp->flag |= FA_MODIFIED;                /* Set file change flag */
04124 
04125     LEAVE_FF(fs, FR_OK);
04126 }
