00027 static int _error_remap(int err) {
00028     switch (err) {
00029     case LFS_ERR_OK:
00030         return 0;
00031     case LFS_ERR_IO:
00032         return -EIO;
00033     case LFS_ERR_NOENT:
00034         return -ENOENT;
00035     case LFS_ERR_EXIST:
00036         return -EEXIST;
00037     case LFS_ERR_NOTDIR:
00038         return -ENOTDIR;
00039     case LFS_ERR_ISDIR:
00040         return -EISDIR;
00041     case LFS_ERR_INVAL:
00042         return -EINVAL;
00043     case LFS_ERR_NOSPC:
00044         return -ENOSPC;
00045     case LFS_ERR_NOMEM:
00046         return -ENOMEM;
00047     case LFS_ERR_CORRUPT:
00048         return -EILSEQ;
00049     default:
00050         return err;
00051     }
00052 }
00053 
00054 static int _flags_remap(int flags) {
00055     return ((((flags & 3) == O_RDONLY) ? LFS_O_RDONLY : 0) |
00056             (((flags & 3) == O_WRONLY) ? LFS_O_WRONLY : 0) |
00057             (((flags & 3) == O_RDWR)   ? LFS_O_RDWR   : 0) |
00058             ((flags & O_CREAT)  ? LFS_O_CREAT  : 0) |
00059             ((flags & O_EXCL)   ? LFS_O_EXCL   : 0) |
00060             ((flags & O_TRUNC)  ? LFS_O_TRUNC  : 0) |
00061             ((flags & O_APPEND) ? LFS_O_APPEND : 0));
00062 }
00063 
00064 static int _whence_remap(int whence) {
00065     switch (whence) {
00066     case SEEK_SET:
00067         return LFS_SEEK_SET;
00068     case SEEK_CUR:
00069         return LFS_SEEK_CUR;
00070     case SEEK_END:
00071         return LFS_SEEK_END;
00072     default:
00073         return whence;
00074     }
00075 }
00076 
00077 static int _mode_remap(int type) {
00078     int mode = S_IRWXU | S_IRWXG | S_IRWXO;
00079     switch (type) {
00080     case LFS_TYPE_DIR:
00081         return mode | S_IFDIR;
00082     case LFS_TYPE_REG:
00083         return mode | S_IFREG;
00084     default:
00085         return 0;
00086     }
00087 }
00088 
00089 static int _type_remap(int type) {
00090     switch (type) {
00091     case LFS_TYPE_DIR:
00092         return DT_DIR;
00093     case LFS_TYPE_REG:
00094         return DT_REG;
00095     default:
00096         return DT_UNKNOWN;
00097     }
00098 }
