00124 static void _init_config(struct lfs_config *config, blockdevice_t *device) {
00125     int32_t block_cycles = config->block_cycles;
00126     lfs_size_t lookahead_size = config->lookahead_size;
00127     memset(config, 0, sizeof(struct lfs_config));
00128     config->block_cycles = block_cycles;
00129     config->lookahead_size = lookahead_size;
00130 
00131     config->read = littlefs_read;
00132     config->prog = littlefs_program;
00133     config->erase = littlefs_erase;
00134     config->sync = littlefs_sync;
00135     config->read_size = device->read_size;
00136     config->prog_size = device->program_size;
00137     config->block_size = device->erase_size;
00138     config->block_count = device->size(device) / config->block_size;
00139     config->cache_size = device->erase_size;
00140     config->context = device;
00141 }
