00143 static int format(filesystem_t *fs, blockdevice_t *device) {
00144     filesystem_littlefs_context_t *context = fs->context;
00145     mutex_enter_blocking(&context->_mutex);
00146 
00147     int err = device->init(device);
00148     if (err) {
00149         mutex_exit(&context->_mutex);
00150         return err;
00151     }
00152 
00153     // erase super block
00154     err = device->erase(device, 0, device->program_size);
00155     if (err) {
00156         mutex_exit(&context->_mutex);
00157         return err;
00158     }
00159 
00160     _init_config(&context->config, device);
00161     err = lfs_format(&context->littlefs, &context->config);
00162     if (err) {
00163         mutex_exit(&context->_mutex);
00164         return _error_remap(err);
00165     }
00166 
00167     mutex_exit(&context->_mutex);
00168     return 0;
00169 }
