00171 static int mount(filesystem_t *fs, blockdevice_t *device, bool pending) {
00172     (void)pending;
00173     filesystem_littlefs_context_t *context = fs->context;
00174     mutex_enter_blocking(&context->_mutex);
00175 
00176     int err = device->init(device);
00177     if (err) {
00178         mutex_exit(&context->_mutex);
00179         return err;
00180     }
00181 
00182     _init_config(&context->config, device);
00183     err = lfs_mount(&context->littlefs, &context->config);
00184     if (err) {
00185         mutex_exit(&context->_mutex);
00186         return _error_remap(err);
00187     }
00188     mutex_exit(&context->_mutex);
00189     return 0;
00190 }
