00192 static int unmount(filesystem_t *fs) {
00193     filesystem_littlefs_context_t *context = fs->context;
00194     mutex_enter_blocking(&context->_mutex);
00195 
00196     int res = 0;
00197     int err = lfs_unmount(&context->littlefs);
00198     if (err && !res) {
00199         res = _error_remap(err);
00200     }
00201 
00202     mutex_exit(&context->_mutex);
00203     return res;
00204 }
