00260 static int file_open(filesystem_t *fs, fs_file_t *file, const char *path, int flags) {
00261     filesystem_littlefs_context_t *context = fs->context;
00262 
00263     littlefs_file_t *f = file->context = calloc(1, sizeof(littlefs_file_t));
00264     if (f == NULL) {
00265         fprintf(stderr, "file_open: Out of memory\n");
00266         return -ENOMEM;
00267     }
00268 
00269     mutex_enter_blocking(&context->_mutex);
00270     int err = lfs_file_open(&context->littlefs, &f->file, path, _flags_remap(flags));
00271     mutex_exit(&context->_mutex);
00272 
00273     if (err) {
00274         free(f);
00275     }
00276     return _error_remap(err);
00277 }
