00369 static int dir_open(filesystem_t *fs, fs_dir_t *dir, const char *path) {
00370     filesystem_littlefs_context_t *context = fs->context;
00371     lfs_dir_t *d = calloc(1, sizeof(lfs_dir_t));
00372     if (d == NULL) {
00373         fprintf(stderr, "dir_open: Out of memory\n");
00374         return -ENOMEM;
00375     }
00376 
00377     mutex_enter_blocking(&context->_mutex);
00378     int err = lfs_dir_open(&context->littlefs, d, path);
00379     mutex_exit(&context->_mutex);
00380 
00381     if (!err) {
00382         dir->context = d;
00383         dir->fd = -1;
00384     } else {
00385         free(d);
00386     }
00387     return _error_remap(err);
00388 }
