00402 static int dir_read(filesystem_t *fs, fs_dir_t *dir, struct dirent *ent) {
00403     filesystem_littlefs_context_t *context = fs->context;
00404     lfs_dir_t *d = dir->context;
00405     struct lfs_info info;
00406 
00407     mutex_enter_blocking(&context->_mutex);
00408     int res = lfs_dir_read(&context->littlefs, d, &info);
00409     mutex_exit(&context->_mutex);
00410 
00411     if (res == 1) {
00412         ent->d_type = _type_remap(info.type);
00413         strcpy(ent->d_name, info.name);
00414         return _error_remap(LFS_ERR_OK);
00415     } else if (res == 0) {
00416         return _error_remap(LFS_ERR_NOENT);
00417     }
00418     return _error_remap(res);
00419 }
