00421 filesystem_t *filesystem_littlefs_create(uint32_t block_cycles,
00422                                          lfs_size_t lookahead_size)
00423 {
00424     filesystem_t *fs = calloc(1, sizeof(filesystem_t));
00425     if (fs == NULL) {
00426         fprintf(stderr, "filesystem_littlefs_create: Out of memory\n");
00427         return NULL;
00428     }
00429 
00430     fs->type = FILESYSTEM_TYPE_LITTLEFS;
00431     fs->name = FILESYSTEM_NAME;
00432     fs->mount = mount;
00433     fs->unmount = unmount;
00434     fs->format = format;
00435     fs->remove = file_remove;
00436     fs->rename = file_rename;
00437     fs->mkdir = file_mkdir;
00438     fs->rmdir = file_rmdir;
00439     fs->stat = file_stat;
00440     fs->file_open = file_open;
00441     fs->file_close = file_close;
00442     fs->file_write = file_write;
00443     fs->file_read = file_read;
00444     fs->file_sync = file_sync;
00445     fs->file_seek = file_seek;
00446     fs->file_tell = file_tell;
00447     fs->file_size = file_size;
00448     fs->file_truncate = file_truncate;
00449     fs->dir_open = dir_open;
00450     fs->dir_close = dir_close;
00451     fs->dir_read = dir_read;
00452 
00453     filesystem_littlefs_context_t *context = calloc(1, sizeof(filesystem_littlefs_context_t));
00454     if (context == NULL) {
00455         fprintf(stderr, "filesystem_littlefs_create: Out of memory\n");
00456         free(fs);
00457         return NULL;
00458     }
00459     context->id = -1;
00460     context->config.block_cycles = block_cycles;
00461     context->config.lookahead_size = lookahead_size;
00462     mutex_init(&context->_mutex);
00463     fs->context = context;
00464     return fs;
00465 }
