00229 void TinyEngineFrameBuffer::drawCircle1(int xc, int yc, int x, int y, uint8_t color)
00230 {
00231     draw_pixel(xc + x, yc + y, color);
00232     draw_pixel(xc - x, yc + y, color);
00233     draw_pixel(xc + x, yc - y, color);
00234     draw_pixel(xc - x, yc - y, color);
00235     draw_pixel(xc + y, yc + x, color);
00236     draw_pixel(xc - y, yc + x, color);
00237     draw_pixel(xc + y, yc - x, color);
00238     draw_pixel(xc - y, yc - x, color);
00239 }
