00241 tinyengine_status_t TinyEngineFrameBuffer::draw_filled_circle(const uint32_t xc, const uint32_t yc, const uint32_t r,
00242     const uint8_t color)
00243 {
00244     int x = 0, y = r;
00245     int d = 3 - 2 * r;
00246     drawCircle1(xc, yc, x, y, color);
00247     while (y >= x)
00248     {
00249         // check for decision parameter
00250         // and correspondingly
00251         // update d, y
00252         if (d > 0)
00253         {
00254             y--;
00255             d = d + 4 * (x - y) + 10;
00256         }
00257         else
00258             d = d + 4 * x + 6;
00259 
00260         // Increment x after updating decision parameter
00261         x++;
00262 
00263         // Draw the circle using the new coordinates
00264         drawCircle1(xc, yc, x, y, color);
00265     }
00266 
00267     return TINYENGINE_OK;
00268 }
