00404 tinyengine_status_t TinyEngineFrameBuffer::draw_char(const char _char, const uint32_t x, const uint32_t y,
00405     const uint8_t _color)
00406 {
00407     _draw_char_temp_index = 0;
00408     // Convert the character to an index
00409     _draw_char_temp_index = _char & 0x7F;
00410     if (_draw_char_temp_index < ' ')
00411     {
00412         _draw_char_temp_index = 0;
00413     }
00414     else
00415     {
00416         _draw_char_temp_index -= ' ';
00417     }
00418     // 'font' is a multidimensional array of [96][char_width]
00419     // which is really just a 1D array of size 96*char_width.
00420     const uint8_t* chr = renew_font[_draw_char_temp_index];
00421     char temp = 0;
00422     for (uint8_t j = 0; j < CHAR_WIDTH; j++)
00423     {
00424         temp |= chr[j];
00425     }
00426 
00427     // Draw pixels
00428     for (uint8_t j = 0; j < CHAR_WIDTH; j++)
00429     {
00430         for (uint8_t i = 0; i < CHAR_HEIGHT; i++)
00431         {
00432             if (chr[j] & (1 << i))
00433             {
00434                 // this added like 100FPS to the game on full optimizations
00435                 // this->pixel_buffer_back[((y + i) * this->m_display_w + (x + j)) * 1] = (uint8_t)(_color);
00436                 memset(pixel_buffer_back + (((y + i) * this->m_display_w + (x + j)) * 1), _color, 1);
00437                 // 300FPS improvement lol.
00438                 // draw_pixel(x + j, y + i, _color);
00439             }
00440         }
00441     }
00442     return TINYENGINE_OK;
00443 }
